const data = $json; // El JSON validado y completo

const info = data.info || {};
const cierre = data.cierre || {};
const messages = data.messages || [];

// Lista de disclaimers a eliminar (puedes agregar más si surgen otros)
const disclaimers = [
  `***** Disclaimer ***** This email (including any attachments) is intended only for the recipient(s) named above. It may contain confidencial or privileged information and should not be read, copied or otherwise used by any other person. If you are not a recipient, please contact the sender and delete the email from your system. ***** Aviso de Confidencialidad ***** Este mensaje, (incluyendo cualquier anexo) está dirigido únicamente a los destinatarios arriba señalados. Puede contener información confidencial o privilegiada y no debe ser leído, copiado o de otra forma utilizado por cualquier otra persona. Si usted recibe esta comunicación por error, favor avisar al remitente y eliminar el mensaje de su sistema.`,
  `***** Disclaimer ***** This email (including any attachments) is intended only for the recipient(s) named above. It may contain confidencial or privileged information and should not be read, copied or otherwise used by any other person. If you are not a recipient, please contact the sender and delete the email from your system.`,
  `***** Aviso de Confidencialidad ***** Este mensaje, (incluyendo cualquier anexo) está dirigido únicamente a los destinatarios arriba señalados. Puede contener información confidencial o privilegiada y no debe ser leído, copiado o de otra forma utilizado por cualquier otra persona. Si usted recibe esta comunicación por error, favor avisar al remitente y eliminar el mensaje de su sistema.`,
];

// Crear encabezado del ticket
let markdown = `# Ticket: ${info.casenumber || 'N/A'}\n\n`;
markdown += `**Cliente:** ${info.customerName || 'N/A'}  \n`;
markdown += `**ID Cliente:** ${info.customerID || 'N/A'}  \n`;
markdown += `**Estado:** ${info.status?.value || 'N/A'}  \n`;
markdown += `**Prioridad:** ${info.customerType || 'N/A'}  \n\n`;

// Sección de interacciones
markdown += `## Historial de Interacciones\n\n`;

if (messages.length > 0) {
  messages
    .filter(msg => msg.message && !msg.message.includes('[IMAGE]'))
    .forEach(msg => {
      const fecha = msg.date || 'Fecha desconocida';

      // Eliminar todos los disclaimers conocidos
      let mensajeLimpio = msg.message;
      disclaimers.forEach(texto => {
        mensajeLimpio = mensajeLimpio.replaceAll(texto, '');
      });

      // Limpiar espacios
      mensajeLimpio = mensajeLimpio
        .replace(/\n/g, ' ')
        .replace(/\s+/g, ' ')
        .trim();

      // Eliminar comillas simples y dobles
      mensajeLimpio = mensajeLimpio.replace(/['"]/g, '');

      markdown += `### ${msg.author?.value || 'Anónimo'} - ${fecha}\n`;
      markdown += `${mensajeLimpio}\n\n`;
    });
} else {
  markdown += "* No hay interacciones registradas\n\n";
}

// Sección de cierre
if (cierre && Object.keys(cierre).length > 0) {
  markdown += `## Detalles de Cierre\n\n`;
  markdown += `**Motivo:** ${cierre.Reason_for_Closing || 'N/A'}  \n`;
  markdown += `**Responsable:** ${cierre.Responsable || 'N/A'}  \n`;
  markdown += `**Descripción:** ${cierre.Incident_Description || 'N/A'}  \n\n`;
}

// Devolver como objeto que n8n espera
return {
  json: {
    markdownContent: markdown,
    ticketId: info.casenumber,
    customerId: info.customerID,
    status: info.status?.value
  }
};

// Acceder correctamente a los datos en n8n
const items = $input.all();
const result = [];

for (let i = 0; i < items.length; i++) {
  // Crear una copia del objeto json
  const newJson = {...items[i].json};

  // Convertir customerId a número
  newJson.customerId = Number(newJson.customerId);

  // Crear un nuevo objeto item con el json modificado
  const newItem = {
    json: newJson
  };

  // Añadir al array de resultados
  result.push(newItem);
}

return result;

const input = $input.first().json.body;

// Validar existencia general
if (!input.info || !input.messages || !input.cierre) {
  throw new Error('Faltan secciones del JSON: se esperaba "info", "messages" y "cierre" en el mismo objeto.');
}

// Función utilitaria para validar campos
function validateField(obj, key, type = 'string', required = true) {
  if (!obj.hasOwnProperty(key)) {
    if (required) throw new Error(`Falta la clave: ${key}`);
    return;
  }
  if (type === 'object') {
    if (typeof obj[key] !== 'object' || obj[key] === null || Array.isArray(obj[key])) {
      throw new Error(`La clave "${key}" debe ser un objeto`);
    }
  } else if (type === 'array') {
    if (!Array.isArray(obj[key])) {
      throw new Error(`La clave "${key}" debe ser un array`);
    }
  } else {
    if (typeof obj[key] !== type) {
      throw new Error(`La clave "${key}" debe ser de tipo ${type}`);
    }
  }
}

// Validar "info"
const info = input.info;

['id', 'title', 'casenumber', 'customerID', 'customerName', 'customerType', 'startdate', 'enddate', 'phonealt']
  .forEach(key => validateField(info, key));

['assigned', 'type', 'type_event', 'creator', 'category', 'services', 'status']
  .forEach(key => {
    validateField(info, key, 'object');
    validateField(info[key], 'id');
    validateField(info[key], 'value');
  });

// Validar "messages"
validateField(input, 'messages', 'array');
input.messages.forEach((msg, i) => {
  try {
    validateField(msg, 'id');
    validateField(msg, 'author', 'object');
    validateField(msg.author, 'id');
    validateField(msg.author, 'value');
    validateField(msg, 'authoremail');
    validateField(msg, 'date');
    validateField(msg, 'datesecs', 'number');
    validateField(msg, 'message');
    // "attachment" es opcional
  } catch (e) {
    throw new Error(`Error en messages[${i}]: ${e.message}`);
  }
});

// Validar "cierre"
const cierre = input.cierre;

[
  'CaseId', 'Company', 'Imputabilidad', 'Responsable',
  'Unavailable_Minutes', 'unavailable_to_report_minutes',
  'Type_of_Unavailable', 'date_upgrade_services_affecte',
  'General_description_cause', 'Servicio', 'Area',
  'person_confirms_closure', 'overview_fall', 'Reason_for_Closing',
  'SubReason_for_Closing', 'Equipment', 'Node', 'Displacement',
  'Reason_Displacement', 'Include_Report', 'Cause_Internal_Error',
  'email_confirm_close', 'Development_Process_Realized',
  'Incident_Description', 'Action_Plan'
].forEach(key => validateField(cierre, key, 'string', false));

// ✅ Si todo es válido, retornar el JSON original
return [{ 
    json: input 
  }];

