// Este c√≥digo asume que el nodo 'Code' recibe un array de √≠tems.
// 'items' es una variable global en n8n que contiene la entrada de datos.

const allUnifiedSummaries = [];

for (const item of items) {
  let currentSummaryParts = [];

  // Obtenemos todas las claves del objeto JSON actual
  const keys = Object.keys(item.json);

  for (const key of keys) {
    // Ignoramos la clave '_id' ya que no forma parte del contenido del resumen
    if (key === '_id') {
      continue;
    }

    // A√±adimos la clave actual a las partes del resumen
    // Aqu√≠ es donde "recolectamos" el texto que est√° en las claves rotas
    currentSummaryParts.push(key);
  }

  // Unimos todas las partes recolectadas con saltos de l√≠nea doble para mantener el formato Markdown
  // Esto reconstruir√° el texto completo del resumen a partir de las claves del JSON
  const unifiedSummary = currentSummaryParts.join('\n\n');
  allUnifiedSummaries.push(unifiedSummary);
}

// Unimos todos los res√∫menes unificados de cada √≠tem en un solo texto final
// Si tienes m√∫ltiples √≠tems de entrada (ej. de diferentes URLs), esto los consolidar√° todos
const finalCombinedSummary = allUnifiedSummaries.join('\n\n---\n\n'); // Separador m√°s claro entre reportes si hay varios

// n8n espera que el nodo Code/Function retorne un array de objetos.
return [
  {
    json: {
      reporte_ciberseguridad_unificado: finalCombinedSummary // La clave ahora contendr√° todo el reporte reconstruido
    }
  }
];
