// Code Node - Dinámico y optimizado para reducir tokens
const services =  $input.first().json.body.services || [];

// Estadísticas básicas
const totalServicios = services.length;
const serviciosActivos = services.filter(s => s.status === 'Activo').length;
const anchoBandaTotal = services.reduce((sum, s) => sum + (parseFloat(s.bandwidthService) || 0), 0);

// Agrupar dinámicamente por ciudad (solo ciudades con servicios)
const ciudades = {};
services.forEach(s => {
  const ciudad = s.cityInstall;
  if (!ciudades[ciudad]) {
    ciudades[ciudad] = { count: 0, banda: 0 };
  }
  ciudades[ciudad].count++;
  ciudades[ciudad].banda += parseFloat(s.bandwidthService) || 0;
});

// Agrupar dinámicamente por país
const paises = {};
services.forEach(s => {
  const pais = s.countryInstall;
  if (!paises[pais]) {
    paises[pais] = { count: 0, banda: 0 };
  }
  paises[pais].count++;
  paises[pais].banda += parseFloat(s.bandwidthService) || 0;
});

// Agrupar dinámicamente por tipo de producto
const tipos = {};
services.forEach(s => {
  const tipo = s.productType;
  if (!tipos[tipo]) {
    tipos[tipo] = 0;
  }
  tipos[tipo]++;
});

// Agrupar dinámicamente por familia
const familias = {};
services.forEach(s => {
  const familia = s.family;
  if (!familias[familia]) {
    familias[familia] = 0;
  }
  familias[familia]++;
});

// Crear texto ultra-compacto dinámico
const ciudadesTexto = Object.entries(ciudades)
  .map(([ciudad, data]) => `${ciudad}:${data.count}(${data.banda}Mbps)`)
  .join(',');

const paisesTexto = Object.entries(paises)
  .map(([pais, data]) => `${pais}:${data.count}(${data.banda}Mbps)`)
  .join(',');

const tiposTexto = Object.entries(tipos)
  .map(([tipo, count]) => `${tipo}:${count}`)
  .join(',');

const familiasTexto = Object.entries(familias)
  .map(([familia, count]) => `${familia}:${count}`)
  .join(',');

// Lista compacta de servicios (incluyendo dirección)
const serviciosLista = services.map(s => 
  `${s.code}|${s.productType}|${s.cityInstall}|${s.addressInstall}|${s.status}|${parseFloat(s.bandwidthService) || 0}Mbps`
).join(' ');

// Texto final súper compacto
const textoFinal = `Total:${totalServicios},Activos:${serviciosActivos},Banda:${anchoBandaTotal}Mbps. Ciudades:[${ciudadesTexto}]. Paises:[${paisesTexto}]. Tipos:[${tiposTexto}]. Familias:[${familiasTexto}]. Servicios:[${serviciosLista}]`;

return [{
  json: {
    texto_para_ia: textoFinal,
    // Datos estructurados para consultas específicas
    resumen: {
      total: totalServicios,
      activos: serviciosActivos,
      banda_total: anchoBandaTotal,
      por_ciudad: ciudades,
      por_pais: paises,
      por_tipo: tipos,
      por_familia: familias
    }
  }
}];
