// Funciones auxiliares
function renderStats(vulnerabilidadesPorCriticidad) {
  const contadores = {
    'ALTA': vulnerabilidadesPorCriticidad['ALTA'].length,
    'MEDIA': vulnerabilidadesPorCriticidad['MEDIA'].length,
    'BAJA': vulnerabilidadesPorCriticidad['BAJA'].length
  };

  const totalVulnerabilidades = contadores.ALTA + contadores.MEDIA + contadores.BAJA;

  return `
  <table width="100%" border="0" cellpadding="0" cellspacing="0">
    <tr>
      <td style="padding: 5px;">
        <table width="100%" border="0" cellpadding="10" cellspacing="5" style="background-color: #f8f9fa; border-radius: 5px;">
          <tr>
            <td align="center" valign="top" style="width: 25%;">
              <div style="font-size: 24px; font-weight: bold; color: #dc3545; margin-bottom: 5px;">${contadores.ALTA}</div>
              <div style="font-size: 12px; color: #666;">Vulnerabilidades de Alta Criticidad</div>
            </td>
            <td align="center" valign="top" style="width: 25%;">
              <div style="font-size: 24px; font-weight: bold; color: #ff9933; margin-bottom: 5px;">${contadores.MEDIA}</div>
              <div style="font-size: 12px; color: #666;">Vulnerabilidades de Media Criticidad</div>
            </td>
            <td align="center" valign="top" style="width: 25%;">
              <div style="font-size: 24px; font-weight: bold; color: #28a745; margin-bottom: 5px;">${contadores.BAJA}</div>
              <div style="font-size: 12px; color: #666;">Vulnerabilidades de Baja Criticidad</div>
            </td>
            <td align="center" valign="top" style="width: 25%;">
              <div style="font-size: 24px; font-weight: bold; color: #2c67b2; margin-bottom: 5px;">${totalVulnerabilidades}</div>
              <div style="font-size: 12px; color: #666;">Total de Vulnerabilidades</div>
            </td>
          </tr>
        </table>
      </td>
    </tr>
  </table>`;
}

function generarHTMLVulnerabilidad(vuln) {
  let cvssColor;
  let cvssBackColor;
  let cvssValue;

  // Manejar el caso especial de CVSS "TBD"
  if (vuln.cvss === "TBD" || vuln.cvss === null || vuln.cvss === undefined) {
    cvssColor = '#ff9933'; // Naranja
    cvssBackColor = '#fff5e6';
    cvssValue = "Pendiente";
  } else {
    // Manejar valores numéricos normales
    if (vuln.cvss >= 7) {
      cvssColor = '#dc3545';
      cvssBackColor = '#ffeaea';
    } else if (vuln.cvss >= 4) {
      cvssColor = '#ff9933';
      cvssBackColor = '#fff5e6';
    } else {
      cvssColor = '#28a745';
      cvssBackColor = '#e6f5e6';
    }
    cvssValue = vuln.cvss;
  }

  // Generar HTML para acciones recomendadas
  let accionesHTML = '';
  if (vuln.acciones_recomendadas && vuln.acciones_recomendadas.length > 0) {
    accionesHTML = vuln.acciones_recomendadas.map(accion => 
      `<li style="margin-bottom: 5px;">${accion}</li>`
    ).join('');
  }

  // Generar HTML para referencias
  let referenciasHTML = '';
  if (vuln.referencias && vuln.referencias.length > 0) {
    referenciasHTML = vuln.referencias.map(ref => 
      `<a href="${ref}" style="color: #2c67b2; font-size: 13px; word-break: break-all; display: block; margin-bottom: 5px;">${ref}</a>`
    ).join('');
  }

  return `
  <table width="100%" border="0" cellpadding="0" cellspacing="0" style="margin-bottom: 20px; border: 1px solid #eaeaea; border-radius: 5px;">
    <tr>
      <td style="padding: 15px 20px;">
        <table width="100%" border="0" cellpadding="0" cellspacing="0">
          <tr>
            <td>
              <div style="font-weight: bold; font-size: 18px; color: #2c67b2; margin-bottom: 8px;">${vuln.fabricante || 'Fabricante no especificado'}</div>
              <div style="font-size: 14px; color: #666; margin-top: 5px;">
                <span><strong>CVE:</strong> ${vuln.cve}</span><br>
                <span><strong>Fecha del Reporte:</strong> ${vuln.fecha_reporte}</span><br>
                <span><strong>Producto Afectado:</strong> ${vuln.producto}</span><br>
                <span style="display: inline-block; padding: 3px 8px; border-radius: 3px; font-weight: bold; font-size: 12px; margin-top: 5px; background-color: ${cvssBackColor}; color: ${cvssColor};">CVSS: ${cvssValue}</span>
              </div>
            </td>
          </tr>
          <tr>
            <td style="padding-top: 15px;">
              <table width="100%" border="0" cellpadding="12" cellspacing="0" style="background-color: #f8f9fa; border-radius: 5px; margin: 10px 0;">
                <tr>
                  <td>
                    <div style="font-weight: bold; margin-bottom: 5px; color: #555;">Descripción Técnica</div>
                    <p>${vuln.descripcion}</p>
                  </td>
                </tr>
              </table>
            </td>
          </tr>
          <tr>
            <td style="padding-top: 15px;">
              <div style="font-weight: bold; margin-bottom: 5px; color: #555;">Acciones Recomendadas</div>
              <ul style="padding-left: 25px; margin-bottom: 15px;">
                ${accionesHTML}
              </ul>
            </td>
          </tr>
          <tr>
            <td>
              <div style="font-weight: bold; margin-bottom: 5px; color: #555;">Referencias</div>
              ${referenciasHTML}
            </td>
          </tr>
        </table>
      </td>
    </tr>
  </table>`;
}

// Código principal para n8n
const items = $input.all();
const returnItems = [];

for (const item of items) {
  try {
    // Obtener el período desde los parámetros de entrada (si existen)
    // Usar item.json directamente, sin confiar en nombres de nodos anteriores
    const startDate = item.json.startDate || null;
    const endDate = item.json.endDate || null;

    console.log("Período recibido:", { startDate, endDate });

    // Extraer y parsear los datos JSON
    let securityData;

    if (typeof item.json.output === 'string' && item.json.output.trim().startsWith('{')) {
      try {
        securityData = JSON.parse(item.json.output);
      } catch (e) {
        console.log('Error al parsear JSON:', e);
        throw new Error(`No se pudo parsear el JSON: ${e.message}`);
      }
    } else {
      throw new Error('No se encontró un JSON válido en el campo output');
    }

    // Validar la estructura
    if (!securityData || !securityData.vulnerabilidades || !Array.isArray(securityData.vulnerabilidades)) {
      throw new Error('Formato JSON inválido: se requiere un array de vulnerabilidades');
    }

    // Normalizar criticidad a mayúsculas
    securityData.vulnerabilidades.forEach(vuln => {
      if (vuln.criticidad) {
        vuln.criticidad = vuln.criticidad.toUpperCase();
      }
    });

    // Clasificar vulnerabilidades por criticidad
    const vulnerabilidadesPorCriticidad = {
      'ALTA': [],
      'MEDIA': [],
      'BAJA': []
    };

    // Procesar cada vulnerabilidad 
    securityData.vulnerabilidades.forEach(vuln => {
      // Convertir a mayúsculas y normalizar
      const criticidad = String(vuln.criticidad || '').toUpperCase();

      // Añadir a la categoría apropiada si existe
      if (criticidad === 'ALTA') {
        vulnerabilidadesPorCriticidad.ALTA.push(vuln);
      } else if (criticidad === 'MEDIA') {
        vulnerabilidadesPorCriticidad.MEDIA.push(vuln);
      } else if (criticidad === 'BAJA') {
        vulnerabilidadesPorCriticidad.BAJA.push(vuln);
      }
    });

    // Formatear fecha actual
    const ahora = new Date();
    const meses = ['Enero', 'Febrero', 'Marzo', 'Abril', 'Mayo', 'Junio', 'Julio', 'Agosto', 'Septiembre', 'Octubre', 'Noviembre', 'Diciembre'];
    const fechaFormateada = `${meses[ahora.getMonth()]} ${ahora.getDate()} de ${ahora.getFullYear()}`;

    // Total de vulnerabilidades
    const totalVulnerabilidades = securityData.vulnerabilidades.length;

    // Construir el HTML - usando tablas para compatibilidad con correo
    let html = `<!DOCTYPE html>
<html lang="es">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Boletín de Alertas de Ciberseguridad</title>
</head>
<body style="margin: 0; padding: 0; background-color: #f8f9fa; color: #333; font-family: Arial, sans-serif; line-height: 1.6;">
    <div style="max-width: 800px; margin: 0 auto; background-color: #ffffff; box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);">
        <!-- Cabecera con gradiente y período -->
        ${createEmailSafeHeader(fechaFormateada, startDate, endDate)}

        <!-- Contenido -->
        <div style="padding: 30px;">
            <div style="background-color: #f1f5f9; border-left: 4px solid #2c67b2; padding: 12px 20px; margin-bottom: 25px; font-size: 14px;">
                <strong>FUENTE DEL REPORTE:</strong> ${securityData.metadata.fuente || 'Fuente no disponible'}
            </div>

            <!-- Estadísticas -->
            ${renderStats(vulnerabilidadesPorCriticidad)}`;

    // Si no hay vulnerabilidades, mostrar mensaje especial
    if (totalVulnerabilidades === 0) {
      html += `
            <div style="background-color: #f8f9fa; padding: 30px; margin-bottom: 30px; text-align: center; border-radius: 5px; font-size: 18px;">
                <strong>¡Buenas noticias!</strong><br>No se han detectado vulnerabilidades en el período actual.
            </div>`;
    } else {
      // Sección de Alta Criticidad
      html += `
            <div style="margin-bottom: 30px;">
                <div style="display: flex; align-items: center; margin-bottom: 15px; padding-bottom: 8px; border-bottom: 1px solid #dee2e6;">
