      if (vulnerabilidadesPorCriticidad['BAJA'].length === 0) {
        html += `
                <div style="background-color: #f8f9fa; padding: 15px; text-align: center; border-radius: 5px; margin-bottom: 20px; color: #666; font-style: italic;">
                    No se han detectado vulnerabilidades de este nivel de criticidad en el período actual.
                </div>`;
      } else {
        vulnerabilidadesPorCriticidad['BAJA'].forEach(vuln => {
          html += generarHTMLVulnerabilidad(vuln);
        });
      }

      html += `
            </div>`;
    }

    // Sección de Pautas Generales
    html += `
            <div style="background-color: #f1f5f9; border-radius: 5px; padding: 20px; margin-top: 30px;">
                <h3 style="font-size: 18px; font-weight: bold; margin-bottom: 15px; color: #1a2e4a;">PAUTAS DE ACCIÓN GENERALES</h3>
                <ol style="padding-left: 25px;">`;

    if (securityData.pautas_generales && securityData.pautas_generales.length > 0) {
      securityData.pautas_generales.forEach(pauta => {
        html += `
                    <li style="margin-bottom: 10px;">${pauta}</li>`;
      });
    } else {
      html += `
                    <li style="margin-bottom: 10px;">No hay pautas generales disponibles.</li>`;
    }

    html += `
                </ol>
            </div>
        </div>

        <div style="text-align: center; margin-top: 30px;">
            <img src="https://github.com/ifxnetworks/Ansible/blob/main/Imagen1nnlnl.jpg?raw=true" alt="Pie de página" style="max-width: 100%; height: auto;">
        </div>
    </div>
</body>
</html>`;

    // Incluir los datos JSON en un campo oculto del HTML
    const jsonScriptTag = `<script id="security-data" type="application/json" style="display:none;">${JSON.stringify(securityData)}</script>`;
    html = html.replace('</body>', `${jsonScriptTag}</body>`);

    // Devolver un único output
    returnItems.push({
      json: {
        htmlEmail: html
      }
    });
  } catch (error) {
    console.log('Error:', error);
    const errorMessage = error instanceof Error 
      ? error.message 
      : 'Error desconocido';
    returnItems.push({
      json: {
        error: `Error al procesar los datos: ${errorMessage}`
      }
    });
  }
}

return returnItems;
