// C√≥digo para nodo Code de n8n - Generador de Alertas de Ciberseguridad
// Procesa datos din√°micos de entrada y genera un email HTML compatible con Outlook y Gmail
// Incluye secci√≥n de eventos relacionados destacada

// Funci√≥n para crear el header compatible con email
function createEmailSafeHeader() {
  return `
  <!-- Header para clientes de correo -->
  <table width="100%" border="0" cellpadding="0" cellspacing="0" style="margin-bottom: 0;" bgcolor="#ffffff">
    <tr>
      <td align="center" valign="top" style="padding: 0;">
        <table width="100%" border="0" cellpadding="0" cellspacing="0" style="max-width: 1000px;">
          <tr>
            <td align="center" valign="top" style="height: 250px; background: url('https://github.com/ifxnetworks/Dise-o/blob/main/Header_Comunicado_Cyberseguridad_IFX_CL_(2.0).png?raw=true') no-repeat center center; background-size: contain;" background="https://github.com/ifxnetworks/Dise-o/blob/main/Header_Comunicado_Cyberseguridad_IFX_CL_(2.0).png?raw=true">
              <!-- Espacio para el header -->
              &nbsp;
            </td>
          </tr>
        </table>
      </td>
    </tr>
  </table>`;
}

// Funci√≥n para crear la secci√≥n de eventos relacionados
function createRelatedEventsSection(eventos) {
  if (!eventos || !eventos.length) {
    return '';
  }

  let eventosHtml = '';

  eventos.forEach(evento => {
    // Formatear fecha
    let fechaFormateada = evento.fecha;
    try {
      const fecha = new Date(evento.fecha);
      fechaFormateada = fecha.toLocaleString();
    } catch (e) {
      console.log('Error al formatear fecha:', e);
    }

    eventosHtml += `
    <tr>
      <td style="padding: 10px; border-bottom: 1px solid #f7d6d6;">
        <table width="100%" border="0" cellpadding="0" cellspacing="0">
          <tr>
            <td width="100" style="font-family: Arial, sans-serif; font-weight: bold; color: #721c24;">Ticket:</td>
            <td style="font-family: Arial, sans-serif;">${evento.ticket || 'No especificado'}</td>
          </tr>
          <tr>
            <td width="100" style="font-family: Arial, sans-serif; font-weight: bold; color: #721c24;">Fecha:</td>
            <td style="font-family: Arial, sans-serif;">${fechaFormateada}</td>
          </tr>
          <tr>
            <td width="100" valign="top" style="font-family: Arial, sans-serif; font-weight: bold; color: #721c24;">Descripci√≥n:</td>
            <td style="font-family: Arial, sans-serif;">${evento.descripcion || 'No disponible'}</td>
          </tr>
        </table>
      </td>
    </tr>`;
  });

  return `
  <table width="100%" border="0" cellpadding="0" cellspacing="0" style="margin-bottom: 30px; margin-top: 0;">
    <tr>
      <td style="background-color: #f8d7da; color: #721c24; padding: 10px; font-family: Arial, sans-serif; font-size: 18px; font-weight: bold; border-left: 5px solid #dc3545;" bgcolor="#f8d7da">
        ‚ö†Ô∏è EVENTOS RELACIONADOS
      </td>
    </tr>
    ${eventosHtml}
  </table>`;
}

// Funci√≥n para crear tarjetas de informaci√≥n
function createInfoCard(title, value) {
  return `
  <table width="100%" border="0" cellpadding="0" cellspacing="0" style="margin-bottom: 10px;">
    <tr>
      <td style="background-color: #f8fafd; border: 1px solid #d1d8e0; padding: 15px;" bgcolor="#f8fafd">
        <table width="100%" border="0" cellpadding="0" cellspacing="0">
          <tr>
            <td style="font-family: Arial, sans-serif; color: #002244; font-weight: bold;">${title}:</td>
          </tr>
          <tr>
            <td style="font-family: Arial, sans-serif; padding-top: 5px;">${value}</td>
          </tr>
        </table>
      </td>
    </tr>
  </table>`;
}

// Funci√≥n para crear la secci√≥n de estado del evento
function createEventStatus(status, severity) {
  let statusColor = "#0066cc";

  if (severity && severity.includes("HIGH")) {
    statusColor = "#dc3545";
  } else if (severity && severity.includes("MEDIUM")) {
    statusColor = "#ff9933";
  } else if (severity && severity.includes("LOW")) {
    statusColor = "#28a745";
  }

  return `
  <table width="100%" border="0" cellpadding="0" cellspacing="0" style="margin-top: 20px;">
    <tr>
      <td style="background-color: ${statusColor}; color: #ffffff; font-family: Arial, sans-serif; text-align: center; padding: 15px; font-weight: bold;" bgcolor="${statusColor}" align="center">
        Estado del Evento: ${status}
      </td>
    </tr>
  </table>`;
}

// Funci√≥n para crear una secci√≥n con t√≠tulo
function createSection(title, content) {
  return `
  <table width="100%" border="0" cellpadding="0" cellspacing="0" style="margin-bottom: 30px;">
    <tr>
      <td>
        <table width="100%" border="0" cellpadding="0" cellspacing="0" style="margin-bottom: 15px;">
          <tr>
            <td width="5" style="background-color: #0066cc;" bgcolor="#0066cc">&nbsp;</td>
            <td style="padding-left: 10px; font-family: Arial, sans-serif; font-size: 20px; font-weight: bold; color: #002244;">
              ${title}
            </td>
          </tr>
        </table>
        ${content}
      </td>
    </tr>
  </table>`;
}

// Funci√≥n para crear una lista compatible con email
function createList(items) {
  if (!items || !items.length) return '<p style="font-family: Arial, sans-serif; margin: 0; padding: 0;">No hay elementos disponibles.</p>';

  let listHtml = '<table width="100%" border="0" cellpadding="0" cellspacing="0">';

  items.forEach(item => {
    listHtml += `
    <tr>
      <td width="20" valign="top" style="font-family: Arial, sans-serif; padding: 5px;">‚Ä¢</td>
      <td style="font-family: Arial, sans-serif; padding: 5px;">${item}</td>
    </tr>`;
  });

  listHtml += '</table>';
  return listHtml;
}

// Funci√≥n para generar el HTML de la alerta
function generateAlertHTML(alertData) {
  // Funci√≥n para acceso seguro a propiedades anidadas
  const getNestedValue = (obj, path, defaultValue = '') => {
    try {
      const result = path.split('.').reduce((o, p) => o?.[p], obj);
      return result !== undefined && result !== null ? result : defaultValue;
    } catch (e) {
      return defaultValue;
    }
  };

  // Secci√≥n de eventos relacionados
  const eventosRelacionadosSection = createRelatedEventsSection(alertData.eventosRelacionados || []);

  // Secci√≥n de informaci√≥n del cliente
  let clientInfoContent = `
  <table width="100%" border="0" cellpadding="0" cellspacing="0">
    <tr>
      <td width="50%" style="padding-right: 10px;">
        ${createInfoCard("Cliente Afectado", getNestedValue(alertData, 'informacionCliente.clienteAfectado', 'No especificado'))}
      </td>
      <td width="50%" style="padding-left: 10px;">
        ${createInfoCard("Fecha del Evento", getNestedValue(alertData, 'informacionCliente.fechaEvento', 'No especificada'))}
      </td>
    </tr>
    <tr>
      <td width="50%" style="padding-right: 10px; padding-top: 10px;">
        ${createInfoCard("N¬∞ Ticket", getNestedValue(alertData, 'informacionCliente.numeroTicket', 'No especificado'))}
      </td>
      <td width="50%" style="padding-left: 10px; padding-top: 10px;">
        ${createInfoCard("Severidad", getNestedValue(alertData, 'informacionCliente.severidad', 'No especificada'))}
      </td>
    </tr>
  </table>

  <table width="100%" border="0" cellpadding="0" cellspacing="0" style="margin-top: 20px;">
    <tr>
      <td width="33%" style="padding-right: 10px;">
        ${createInfoCard("Analista", getNestedValue(alertData, 'informacionAnalisis.analista', 'No especificado'))}
      </td>
      <td width="33%" style="padding: 0 5px;">
        ${createInfoCard("Categor√≠a", getNestedValue(alertData, 'informacionAnalisis.categoria', 'No especificada'))}
      </td>
      <td width="33%" style="padding-left: 10px;">
        ${createInfoCard("Regla Aplicada", getNestedValue(alertData, 'informacionAnalisis.reglaAplicada', 'No especificada'))}
      </td>
    </tr>
  </table>`;

  // Secci√≥n de detalles del evento
  let eventDetailsContent = `
  <table width="100%" border="0" cellpadding="0" cellspacing="0">
    <tr>
      <td width="50%" style="padding-right: 10px;">
        ${createInfoCard("IP de Origen", getNestedValue(alertData, 'detallesEvento.origen.ip', 'No disponible'))}
      </td>
      <td width="50%" style="padding-left: 10px;">
        ${createInfoCard("Hostname de Origen", getNestedValue(alertData, 'detallesEvento.origen.hostname', 'No disponible'))}
      </td>
    </tr>
    <tr>
      <td width="50%" style="padding-right: 10px; padding-top: 10px;">
        ${createInfoCard("IP de Destino", getNestedValue(alertData, 'detallesEvento.destino.ip', 'No disponible'))}
      </td>
      <td width="50%" style="padding-left: 10px; padding-top: 10px;">
        ${createInfoCard("Host de Destino", getNestedValue(alertData, 'detallesEvento.destino.hostname', 'No disponible'))}
      </td>
    </tr>
    <tr>
      <td colspan="2" style="padding-top: 10px;">
        ${createInfoCard("Usuario Afectado", getNestedValue(alertData, 'detallesEvento.usuarioAfectado', 'No especificado'))}
      </td>
    </tr>
  </table>
  ${createEventStatus(
    getNestedValue(alertData, 'detallesEvento.estadoEvento', 'Pendiente de revisi√≥n'), 
    getNestedValue(alertData, 'informacionCliente.severidad', '')
  )}`;

  // Secci√≥n de descripci√≥n y recomendaciones
  let descriptionContent = `
  <table width="100%" border="0" cellpadding="0" cellspacing="0" style="margin-bottom: 20px;">
    <tr>
      <td style="font-family: 'Montserrat', Arial, sans-serif; padding-bottom: 10px;">
        <strong>Descripci√≥n del Evento:</strong> ${getNestedValue(alertData, 'descripcion', 'No hay descripci√≥n disponible')}
      </td>
    </tr>
  </table>

  <table width="100%" border="0" cellpadding="0" cellspacing="0" style="margin-bottom: 20px;">
    <tr>
      <td style="font-family: 'Montserrat', Arial, sans-serif; font-weight: bold; color: #002244; padding-bottom: 10px;">
        Acciones Detectadas:
      </td>
    </tr>
    <tr>
      <td>
        ${createList(alertData.accionesDetectadas || [])}
      </td>
    </tr>
  </table>

  <table width="100%" border="0" cellpadding="0" cellspacing="0">
    <tr>
      <td style="font-family: 'Montserrat', Arial, sans-serif; font-weight: bold; color: #002244; padding-bottom: 10px;">
        Recomendaciones:
      </td>
    </tr>
    <tr>
      <td>
        ${createList(alertData.recomendaciones || [])}
      </td>
    </tr>
  </table>`;

  // Construir HTML completo
  return `<!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Transitional//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd">
<html xmlns="http://www.w3.org/1999/xhtml" lang="es">
<head>
  <meta http-equiv="Content-Type" content="text/html; charset=UTF-8" />
  <title>${getNestedValue(alertData, 'tipoAlerta', 'Alerta de Ciberseguridad')} - ${getNestedValue(alertData, 'titulo', 'Informe de Seguridad')}</title>
  <!--[if mso]>
  <style type="text/css">
    body, table, td {font-family: Arial, Helvetica, sans-serif !important;}
  </style>
  <![endif]-->
  <style type="text/css">
    /* Uso directo de fuentes web-safe en lugar de Google Fonts */

    /* Estilos b√°sicos compatibles con Outlook/Gmail */
    body {
      font-family: Arial, sans-serif;
      line-height: 1.6;
      margin: 0;
      padding: 0;
      background-color: #f4f7fb;
    }
  </style>
</head>
<body style="margin: 0; padding: 20px; background-color: #f4f7fb; -webkit-text-size-adjust: 100%; -ms-text-size-adjust: 100%;" bgcolor="#f4f7fb">
  <table align="center" border="0" cellpadding="0" cellspacing="0" width="100%" style="max-width: 1000px; margin: 0 auto;">
    <tr>
      <td>
        <table align="center" border="0" cellpadding="0" cellspacing="0" width="100%" style="background-color: #ffffff; margin: 0 auto; box-shadow: 0 6px 16px rgba(0, 0, 0, 0.1);" bgcolor="#ffffff">
          <!-- Header -->
          <tr>
            <td align="center">
              ${createEmailSafeHeader()}
            </td>
          </tr>

          <!-- Contenido principal -->
          <tr>
            <td style="padding: 30px;">
              <!-- Eventos Relacionados (si existen) -->
              ${eventosRelacionadosSection}

              <!-- Informaci√≥n del Cliente y Evento -->
              ${createSection("Informaci√≥n del Cliente y Evento", clientInfoContent)}

              <!-- Detalles del Evento -->
              ${createSection("Detalles del Evento", eventDetailsContent)}

              <!-- Descripci√≥n y Recomendaciones -->
              ${createSection("Descripci√≥n y Recomendaciones", descriptionContent)}
            </td>
          </tr>

          <!-- Footer -->
          <tr>
            <td>
              <table width="100%" border="0" cellpadding="0" cellspacing="0">
                <tr>
                  <td align="center">
                    <img src="https://github.com/ifxnetworks/Dise-o/blob/main/Footer_Comunicado_Cyberseguridad_IFX_CL(2.0).png?raw=true" alt="Pie de p√°gina" width="1000" style="width: 100%; max-width: 1000px; height: auto; display: block; border: 0;">
                  </td>
                </tr>
              </table>
            </td>
          </tr>
        </table>
      </td>
    </tr>
  </table>
</body>
</html>`;
}

// Funci√≥n para completar datos faltantes en la alerta
function completarDatosAlerta(datos) {
  // Estructura base para asegurar integridad del reporte
  const plantilla = {
    "tipoAlerta": "ALERTA DE CIBERSEGURIDAD",
    "titulo": "Alerta de Seguridad",
    "informacionCliente": {
      "clienteAfectado": "No especificado",
      "fechaEvento": new Date().toLocaleString(),
      "numeroTicket": "Sin asignar",
      "severidad": "No especificada"
    },
    "informacionAnalisis": {
      "analista": "No especificado",
      "categoria": "No especificada",
      "reglaAplicada": "No especificada"
    },
    "detallesEvento": {
      "origen": {
        "ip": "No disponible",
        "hostname": "No disponible"
      },
      "destino": {
        "ip": "No disponible",
        "hostname": "No disponible"
      },
      "usuarioAfectado": "No especificado",
      "estadoEvento": "Pendiente de revisi√≥n"
    },
    "descripcion": "No hay descripci√≥n disponible",
    "accionesDetectadas": ["No hay acciones detectadas disponibles"],
    "recomendaciones": ["No hay recomendaciones disponibles"],
    "eventosRelacionados": []
  };

  // Combinar datos proporcionados con plantilla
  return {
    ...plantilla,
    ...datos,
    informacionCliente: {
      ...plantilla.informacionCliente,
      ...(datos.informacionCliente || {})
    },
    informacionAnalisis: {
      ...plantilla.informacionAnalisis,
      ...(datos.informacionAnalisis || {})
    },
    detallesEvento: {
      ...plantilla.detallesEvento,
      ...(datos.detallesEvento || {}),
      origen: {
        ...plantilla.detallesEvento.origen,
        ...(datos.detallesEvento?.origen || {})
      },
      destino: {
        ...plantilla.detallesEvento.destino,
        ...(datos.detallesEvento?.destino || {})
      }
    }
  };
}

// Funci√≥n para generar versi√≥n de texto plano
function generateTextVersion(alertData) {
  // Crear secci√≥n de eventos relacionados si existen
  let eventosRelacionadosText = '';
  if (alertData.eventosRelacionados && alertData.eventosRelacionados.length > 0) {
    eventosRelacionadosText = '\n‚ö†Ô∏è EVENTOS RELACIONADOS\n';

    alertData.eventosRelacionados.forEach(evento => {
      let fechaFormateada = evento.fecha;
      try {
        const fecha = new Date(evento.fecha);
        fechaFormateada = fecha.toLocaleString();
      } catch (e) {
        console.log('Error al formatear fecha:', e);
      }

      eventosRelacionadosText += `Ticket: ${evento.ticket || 'No especificado'}\n`;
      eventosRelacionadosText += `Fecha: ${fechaFormateada}\n`;
      eventosRelacionadosText += `Descripci√≥n: ${evento.descripcion || 'No disponible'}\n\n`;
    });
  }

  return `
ALERTA DE CIBERSEGURIDAD - ${alertData.titulo || 'Alerta de Seguridad'}
${eventosRelacionadosText}
Fecha: ${alertData.informacionCliente?.fechaEvento || 'No especificada'}
Cliente: ${alertData.informacionCliente?.clienteAfectado || 'No especificado'}
Ticket: ${alertData.informacionCliente?.numeroTicket || 'No especificado'}
Severidad: ${alertData.informacionCliente?.severidad || 'No especificada'}

DETALLES DEL EVENTO:
- IP Origen: ${alertData.detallesEvento?.origen?.ip || 'No disponible'} (${alertData.detallesEvento?.origen?.hostname || 'No disponible'})
- IP Destino: ${alertData.detallesEvento?.destino?.ip || 'No disponible'} (${alertData.detallesEvento?.destino?.hostname || 'No disponible'})
- Usuario Afectado: ${alertData.detallesEvento?.usuarioAfectado || 'No especificado'}
- Estado: ${alertData.detallesEvento?.estadoEvento || 'No especificado'}

DESCRIPCI√ìN:
${alertData.descripcion || 'No disponible'}

ACCIONES DETECTADAS:
${alertData.accionesDetectadas?.map(accion => `- ${accion}`).join('\n') || '- No disponibles'}

RECOMENDACIONES:
${alertData.recomendaciones?.map(recomendacion => `- ${recomendacion}`).join('\n') || '- No disponibles'}
  `;
}

// C√≥digo principal para n8n
const items = $input.all();
const returnItems = [];

for (const item of items) {
  try {
    // Extraer y parsear los datos JSON del campo output
    let alertData;

    if (item.json && item.json.output) {
      try {
        // El campo output puede contener c√≥digo con backticks y formato ```json
        let jsonStr = item.json.output;

        // Eliminar ```json y ``` si est√°n presentes
        if (jsonStr.includes('```json')) {
          jsonStr = jsonStr.replace(/```json\n/g, '').replace(/```$/g, '');
        }

        // Intenta parsear el JSON
        alertData = JSON.parse(jsonStr);
        console.log("Datos JSON procesados correctamente");
      } catch (e) {
        console.log('Error al parsear JSON:', e);
        throw new Error(`No se pudo parsear el JSON: ${e.message}`);
      }
    } else {
      throw new Error('No se encontr√≥ un campo output en los datos de entrada');
    }

    // Validar estructura y completar datos faltantes
    if (!alertData || !alertData.tipoAlerta || !alertData.titulo) {
      console.log('Estructura de datos incompleta, completando con valores por defecto');
      alertData = completarDatosAlerta(alertData || {});
    }

    // Generar HTML y versi√≥n de texto
    const html = generateAlertHTML(alertData);
    const textVersion = generateTextVersion(alertData);

    // Devolver resultados
    returnItems.push({
      json: {
        htmlEmail: html,
        textEmail: textVersion,
        alertData: alertData,
        subject: `${alertData.tipoAlerta} - ${alertData.titulo} ${alertData.informacionCliente?.severidad ? `[${alertData.informacionCliente.severidad}]` : ''}`
      }
    });
  } catch (error) {
    console.log('Error:', error);
    const errorMessage = error instanceof Error 
      ? error.message 
      : 'Error desconocido';
    returnItems.push({
      json: {
        error: `Error al procesar los datos: ${errorMessage}`,
        originalInput: item.json ? JSON.stringify(item.json) : 'No hay datos de entrada'
      }
    });
  }
}

return returnItems;

