const nombreCliente = $input.item.json['Nombre del Cliente'];

// Convertir a min√∫sculas y reemplazar espacios y caracteres especiales por guiones bajos
const formatName = nombreCliente
  .toLowerCase()
  .replace(/[^\w\s]/g, '_')  // Reemplaza caracteres especiales con _
