// Función para extraer el código aleatorio de la URL
function extractRandomCode(inputText) {
  try {
    // Verificar si la entrada es undefined o null
    if (!inputText) {
      return { 
        random_code: null,
        error: "La entrada es undefined o null" 
      };
    }

    // Intentar encontrar cualquier URL que contenga "random="
    const urlRegex = /https?:\/\/[^\s]+random=([^\s&]+)/;
    const match = inputText.match(urlRegex);

    if (!match || !match[1]) {
      return { 
        random_code: null,
        error: "No se encontró una URL con parámetro 'random'" 
      };
    }

    // Extraer el código completo después de "random="
    const fullRandomCode = match[1];

    // Extraer la parte antes del primer punto
    const dotIndex = fullRandomCode.indexOf('.');
    if (dotIndex === -1) {
      // Si no hay punto, devolver el código completo
      return { 
        random_code: fullRandomCode,
        error: null 
      };
    }

    // Extraer la parte antes del punto
    const codeBeforeDot = fullRandomCode.substring(0, dotIndex);

    return {
      random_code: codeBeforeDot,
      full_random_code: fullRandomCode,
      error: null
    };
  } catch (error) {
    return {
      random_code: null,
      error: `Error al procesar: ${error.message}`
    };
  }
}

// Obtener la entrada de la variable especificada
const inputText = $('Information Extractor').first()?.json?.output?.funcionalidades_de_la_herramienta;

// Procesar la entrada
const result = extractRandomCode(inputText);

// Devolver el resultado
return [{ json: result }];
