// Convertidor de Markdown a HTML para n8n con estilos inline para Outlook
// Convierte texto Markdown a HTML seg√∫n las especificaciones del documento

// Funci√≥n principal del convertidor
function convertMarkdownToHtml(markdownText) {
  let html = markdownText;

  // Estilos inline para elementos
  const styles = {
    h1: 'color: #1a365d; font-size: 24px; margin: 15px 0 10px 0; padding: 0 0 8px 0; border-bottom: 3px solid #3182ce; text-align: center; font-weight: bold; font-family: Arial, sans-serif;',
    h2: 'color: #2d3748; font-size: 18px; margin: 20px 0 8px 0; padding: 8px 12px; background-color: #ebf8ff; border-left: 4px solid #3182ce; font-weight: bold; font-family: Arial, sans-serif;',
    h3: 'color: #4a5568; font-size: 16px; margin: 15px 0 6px 0; padding: 6px 10px; background-color: #f7fafc; border-left: 3px solid #cbd5e0; font-weight: bold; font-family: Arial, sans-serif;',
    p: 'margin: 0 0 8px 0; color: #4a5568; font-family: Arial, sans-serif; line-height: 1.5;',
    ul: 'margin: 8px 0; padding: 0; font-family: Arial, sans-serif;',
    li: 'margin: 6px 0 0 0; padding: 6px 8px; background-color: #f7fafc; border-left: 3px solid #cbd5e0; list-style: none; color: #4a5568; font-family: Arial, sans-serif;',
    strong: 'color: #2d3748; font-weight: bold;',
    a: 'color: #3182ce; text-decoration: underline;',
    hr: 'border: none; height: 2px; background-color: #e2e8f0; margin: 15px 0;',
    table: 'width: 100%; border-collapse: collapse; margin: 12px 0; font-family: Arial, sans-serif;',
    th: 'background-color: #ebf8ff; color: #2d3748; padding: 8px; text-align: left; border: 1px solid #cbd5e0; font-weight: bold;',
    td: 'padding: 6px 8px; border: 1px solid #e2e8f0; color: #4a5568;',
    code: 'background-color: #f7fafc; padding: 2px 4px; color: #c05621; font-weight: bold; font-family: monospace;',
    blockquote: 'background-color: #f7fafc; padding: 10px; border-left: 4px solid #cbd5e0; margin: 8px 0; color: #4a5568; font-style: italic;'
  };

  // Escapar caracteres HTML primero
  html = html.replace(/&/g, '&amp;')
             .replace(/</g, '&lt;')
             .replace(/>/g, '&gt;');

  // Encabezados con #
  html = html.replace(/^######\s+(.*)$/gm, `<h6 style="${styles.h3}">$1</h6>`);
  html = html.replace(/^#####\s+(.*)$/gm, `<h5 style="${styles.h3}">$1</h5>`);
  html = html.replace(/^####\s+(.*)$/gm, `<h4 style="${styles.h3}">$1</h4>`);
  html = html.replace(/^###\s+(.*)$/gm, `<h3 style="${styles.h3}">$1</h3>`);

  // H2 con estilos especiales para observaciones y riesgos
  html = html.replace(/^##\s+(.*‚úÖ.*Observaciones.*Recomendaciones.*)$/gmi, `<h2 style="color: #22543d; font-size: 18px; margin: 20px 0 8px 0; padding: 8px 12px; background-color: #f0fff4; border-left: 4px solid: #48bb78; font-weight: bold; font-family: Arial, sans-serif;">$1</h2>`);
  html = html.replace(/^##\s+(.*‚ö†Ô∏è.*Riesgos.*Identificados.*)$/gmi, `<h2 style="color: #742a2a; font-size: 18px; margin: 20px 0 8px 0; padding: 8px 12px; background-color: #fff5f5; border-left: 4px solid #e53e3e; font-weight: bold; font-family: Arial, sans-serif;">$1</h2>`);
  html = html.replace(/^##\s+(.*)$/gm, `<h2 style="${styles.h2}">$1</h2>`);

  html = html.replace(/^#\s+(.*)$/gm, `<h1 style="${styles.h1}">$1</h1>`);

  // Encabezados con === y ---
  html = html.replace(/^(.+)\n=+$/gm, `<h1 style="${styles.h1}">$1</h1>`);
  html = html.replace(/^(.+)\n-+$/gm, `<h2 style="${styles.h2}">$1</h2>`);

  // Texto en negrita y cursiva combinado (debe ir antes que los individuales)
  html = html.replace(/\*\*\*([^*]+)\*\*\*/g, `<em><strong style="${styles.strong}">$1</strong></em>`);
  html = html.replace(/___([^_]+)___/g, `<em><strong style="${styles.strong}">$1</strong></em>`);
  html = html.replace(/__\*([^*]+)\*__/g, `<em><strong style="${styles.strong}">$1</strong></em>`);
  html = html.replace(/\*\*_([^_]+)_\*\*/g, `<em><strong style="${styles.strong}">$1</strong></em>`);

  // Texto en negrita
  html = html.replace(/\*\*([^*]+)\*\*/g, `<strong style="${styles.strong}">$1</strong>`);
  html = html.replace(/__([^_]+)__/g, `<strong style="${styles.strong}">$1</strong>`);

  // Texto en cursiva
  html = html.replace(/\*([^*]+)\*/g, '<em>$1</em>');
  html = html.replace(/_([^_]+)_/g, '<em>$1</em>');

  // C√≥digo inline
  html = html.replace(/``([^`]+)``/g, `<code style="${styles.code}">$1</code>`);
  html = html.replace(/`([^`]+)`/g, `<code style="${styles.code}">$1</code>`);

  // Enlaces
  html = html.replace(/\[([^\]]+)\]\(([^)]+)(?:\s+"([^"]+)")?\)/g, (match, text, url, title) => {
    if (title) {
      return `<a href="${url}" title="${title}" style="${styles.a}">${text}</a>`;
    }
    return `<a href="${url}" style="${styles.a}">${text}</a>`;
  });

  // URLs y emails autom√°ticos
  html = html.replace(/<(https?:\/\/[^>]+)>/g, `<a href="$1" style="${styles.a}">$1</a>`);
  html = html.replace(/<([^@\s]+@[^@\s]+\.[^@\s]+)>/g, `<a href="mailto:$1" style="${styles.a}">$1</a>`);

  // Im√°genes
  html = html.replace(/!\[([^\]]*)\]\(([^)]+)(?:\s+"([^"]+)")?\)/g, (match, alt, src, title) => {
    if (title) {
      return `<img src="${src}" alt="${alt}" title="${title}" style="border: 0; height: auto; line-height: 100%; outline: none; text-decoration: none;">`;
    }
    return `<img src="${src}" alt="${alt}" style="border: 0; height: auto; line-height: 100%; outline: none; text-decoration: none;">`;
  });

  // Reglas horizontales
  html = html.replace(/^(\*{3,}|-{3,}|_{3,})$/gm, `<hr style="${styles.hr}">`);

  // Citas en bloque
  html = html.replace(/^>\s*(.*)$/gm, (match, content) => {
    return `<blockquote style="${styles.blockquote}">${content}</blockquote>`;
  });

  // Manejar citas anidadas
  html = html.replace(/<blockquote style="[^"]*"><blockquote style="[^"]*">/g, `<blockquote style="${styles.blockquote}"><blockquote style="${styles.blockquote}">`);

  // Listas desordenadas
  html = html.replace(/^[-*+]\s+(.*)$/gm, `<li style="${styles.li}">$1</li>`);

  // Listas ordenadas
  html = html.replace(/^\d+\.\s+(.*)$/gm, `<li style="${styles.li}">$1</li>`);

  // Tablas
  html = html.replace(/\|(.+)\|/g, (match, content) => {
    const cells = content.split('|').map(cell => cell.trim());
    return '|' + cells.join('|') + '|';
  });

  // Procesar tablas completas
  html = html.replace(/(\|.+\|\n)+/g, (tableMatch) => {
    const rows = tableMatch.trim().split('\n');
    let tableHtml = `<table style="${styles.table}">\n`;

    rows.forEach((row, index) => {
      if (row.includes('---') || row.includes('===')) {
        return; // Saltar l√≠neas separadoras
      }

      const cells = row.split('|').filter(cell => cell.trim() !== '').map(cell => cell.trim());

      if (index === 0 || (index === 1 && !rows[0].includes('---'))) {
        // Primera fila como encabezado
        tableHtml += '  <thead>\n    <tr>\n';
        cells.forEach(cell => {
          tableHtml += `      <th style="${styles.th}">${cell}</th>\n`;
        });
        tableHtml += '    </tr>\n  </thead>\n  <tbody>\n';
      } else {
        // Filas de datos
        tableHtml += '    <tr>\n';
        cells.forEach(cell => {
          tableHtml += `      <td style="${styles.td}">${cell}</td>\n`;
        });
        tableHtml += '    </tr>\n';
      }
    });

    tableHtml += '  </tbody>\n</table>\n';
    return tableHtml;
  });

  // Bloques de c√≥digo
  html = html.replace(/^    (.*)$/gm, `<pre style="background-color: #f7fafc; padding: 15px; margin: 15px 0; border-radius: 4px;"><code style="${styles.code}">$1</code></pre>`);

  // Saltos de l√≠nea simples (un solo \n)
  html = html.replace(/(?<!<\/li>)\n(?!<)/g, '<br>');

  // P√°rrafos (l√≠neas separadas por l√≠neas en blanco)
  html = html.replace(/\n\n+/g, `</p>\n\n<p style="${styles.p}">`);
  html = `<p style="${styles.p}">` + html + '</p>';

  // Saltos de l√≠nea (dos espacios al final de l√≠nea)
  html = html.replace(/  <br>/g, '<br>');

  // Limpiar p√°rrafos vac√≠os
  html = html.replace(/<p style="[^"]*">\s*<\/p>/g, '');

  // Envolver listas en <ul>
  html = html.replace(/(<li style="[^"]*">.*<\/li>\n?)+/gs, (match) => {
    return `<ul style="${styles.ul}">\n${match}</ul>\n`;
  });

  // Caracteres escapados
  html = html.replace(/\\([\\`*_{}[\]<>()#+\-.!|])/g, '$1');

  return html;
}

