// Obtener los datos de entrada
const mensajes = $input.first().json.data[0].mensajes_negativos;

// Funci√≥n para generar el HTML de cada mensaje
function generarHTMLMensaje(chat) {
  return `<table cellpadding="0" cellspacing="0" border="0" width="100%"
 style="border: 1px solid #DDDDDD; border-radius: 8px; margin-bottom: 10px; background-color: #FAFAFA;">
 <tr>
 <td style="padding: 10px;">
 <table cellpadding="0" cellspacing="0" border="0" width="100%">
 <tr>
 <td class="message-header">
 <table cellpadding="0" cellspacing="0" border="0">
 <tr>
 <td valign="middle" style="padding-right: 5px;">
 <icon>üë§</icon>
 </td>
 <td valign="middle">
 <span class="message-sender"
 style="font-weight: bold; color: #333333;">${chat.pushname}</span>
 <span class="message-time"
 style="font-size: 0.85em; color: #777777; margin-left: 10px;">${chat.hora}</span>
 </td>
 </tr>
 </table>
 </td>
 </tr>
 <tr>
 <td class="message-content" style="padding-top: 5px; color: #333333;">
 ${chat.mensaje}
 </td>
 </tr>
 </table>
 </td>
 </tr>
 </table>`;
}

// Parsear el JSON si viene como string
let chatsArray;
if (typeof mensajes === 'string') {
  chatsArray = JSON.parse(mensajes);
} else {
  chatsArray = mensajes;
}

// Generar HTML para todos los mensajes
const htmlCompleto = chatsArray.map(chat => generarHTMLMensaje(chat)).join('\n');

// Retornar el resultado
return {
  json: {
    html_mensajes: htmlCompleto
  }
};

