// Obtener los datos de entrada y parsear si es string
let mensajes = $input.first().json.mensajes_negativos || [];

// Parsear el JSON si viene como string
if (typeof mensajes === 'string') {
  try {
    mensajes = JSON.parse(mensajes);
  } catch (e) {
    console.log('Error parseando JSON:', e);
    mensajes = [];
  }
}

// Verificar que sea un array
if (!Array.isArray(mensajes)) {
  mensajes = [];
}

// Funci√≥n para limpiar mensaje (remover an√°lisis y recomendaciones)
function limpiarMensaje(mensaje) {
  if (!mensaje) return '';

  // Remover todo despu√©s de "## Origen del Sentimiento Negativo"
  let mensajeLimpio = mensaje.split('## Origen del Sentimiento Negativo')[0];

  // Remover todo despu√©s de "## Recomendaci√≥n Preventiva"
  mensajeLimpio = mensajeLimpio.split('## Recomendaci√≥n Preventiva')[0];

  return mensajeLimpio.trim();
}

// Funci√≥n para convertir markdown a HTML (simplificada para mensajes)
function convertirMarkdownAHTML(texto) {
  if (!texto) return '';

  // Limpiar el mensaje primero
  texto = limpiarMensaje(texto);

  let html = texto;

  // Convertir texto en negrita (**texto**)
  html = html.replace(/\*\*(.+?)\*\*/g, '<strong style="font-weight: bold; color: #333333;">$1</strong>');

  // Convertir texto en cursiva (*texto*)
  html = html.replace(/\*(.+?)\*/g, '<em style="font-style: italic;">$1</em>');

  // Convertir saltos de l√≠nea
  html = html.replace(/\n\n/g, '</p><p style="margin: 10px 0; color: #333333; line-height: 1.5;">');
  html = html.replace(/\n/g, '<br>');

  // Envolver en p√°rrafos
  html = '<p style="margin: 10px 0; color: #333333; line-height: 1.5;">' + html + '</p>';

  return html;
}

// Funci√≥n para generar el HTML de cada mensaje
function generarHTMLMensaje(chat) {
  // Convertir el mensaje de markdown a HTML
  const mensajeHTML = convertirMarkdownAHTML(chat.mensaje);

  return `<table cellpadding="0" cellspacing="0" border="0" width="100%"
 style="border: 1px solid #DDDDDD; border-radius: 8px; margin-bottom: 10px; background-color: #FAFAFA;">
 <tr>
 <td style="padding: 10px;">
 <table cellpadding="0" cellspacing="0" border="0" width="100%">
 <tr>
 <td class="message-header">
 <table cellpadding="0" cellspacing="0" border="0">
 <tr>
 <td valign="middle" style="padding-right: 5px;">
 <icon>üë§</icon>
 </td>
 <td valign="middle">
 <span class="message-sender"
 style="font-weight: bold; color: #333333;">${chat.pushname}</span>
 <span class="message-time"
 style="font-size: 0.85em; color: #777777; margin-left: 10px;">${chat.hora}</span>
 </td>
 </tr>
 </table>
 </td>
 </tr>
 <tr>
 <td class="message-content" style="padding-top: 5px; color: #333333;">
 ${mensajeHTML}
 </td>
 </tr>
 </table>
 </td>
 </tr>
 </table>`;
}

// Generar HTML solo para los mensajes extra√≠dos
const htmlCompleto = mensajes.map(chat => generarHTMLMensaje(chat)).join('\n');

// Retornar el resultado
return {
  json: {
    html_mensajes: htmlCompleto,
    total_mensajes: mensajes.length
  }
};

const rawText = $input.first().json.output || '';
const cleanText = rawText.replace(/\\n/g, '\n').replace(/\\t/g, '\t');

function extractMessages(text) {
  const messages = [];

  // Regex para formato - **Pushname**: valor
  const messageRegex = /- \*\*Pushname\*\*:\s*([^\n]+)\n\s*- \*\*Hora\*\*:\s*`?([^`\n]+)`?\n\s*- \*\*Mensaje\*\*:\s*([^-]*?)(?=\n- \*\*Pushname\*\*|$)/g;

  let match;
  while ((match = messageRegex.exec(text)) !== null) {
    messages.push({
      pushname: match[1].trim(),
      hora: match[2].trim(),
      mensaje: match[3].trim()
    });
  }

  return messages;
}

function extractAnalysis(text) {
  const match = text.match(/## Origen del Sentimiento Negativo\n([\s\S]*?)(?=\n### Recomendaci√≥n Preventiva|\n## Recomendaci√≥n Preventiva|$)/);
  let analysis = match ? match[1].trim() : '';

  // Limpiar markdown del an√°lisis
  analysis = analysis.replace(/\\n/g, '\n'); // Convertir \\n a \n
  analysis = analysis.replace(/\*\*/g, ''); // Remover **
  analysis = analysis.replace(/^#+\s*/gm, ''); // Remover # al inicio de l√≠neas

  return analysis;
}

function extractRecommendations(text) {
  const match = text.match(/(?:###|##)\s*Recomendaci√≥n Preventiva\n([\s\S]*?)$/);
  let recommendations = match ? match[1].trim() : '';

  // Limpiar markdown de las recomendaciones
  recommendations = recommendations.replace(/\\n/g, '\n'); // Convertir \\n a \n
  recommendations = recommendations.replace(/\*\*/g, ''); // Remover **
  recommendations = recommendations.replace(/^#+\s*/gm, ''); // Remover # al inicio de l√≠neas

  return recommendations;
}

const messages = extractMessages(cleanText);
const analysis = extractAnalysis(cleanText);
const recommendations = extractRecommendations(cleanText);

// Verificar falso positivo
const isFalsePositive = (
  analysis.toLowerCase().includes('no se detecta') && 
  (analysis.toLowerCase().includes('sentimiento negativo') || analysis.toLowerCase().includes('conflicto'))
) || recommendations.toLowerCase().includes('no se requiere recomendaci√≥n');

if (isFalsePositive) {
  return [{
    json: {
      resultado: 'falso_positivo',
      mensaje: 'No se detect√≥ sentimiento negativo real en el contenido',
      metadata: {
        procesado_en: new Date().toISOString(),
        version_parser: '1.7_js_fixed'
      }
    }
  }];
}

const output = {
  resumen: {
    total_mensajes: messages.length,
    usuarios_involucrados: [...new Set(messages.map(m => m.pushname))],
    rango_temporal: {
      primer_mensaje: messages.length > 0 ? messages[0].hora : null,
      ultimo_mensaje: messages.length > 0 ? messages[messages.length - 1].hora : null
    }
  },
  mensajes: messages,
  analisis_sentimiento: {
    origen: analysis,
    texto_completo: analysis
  },
  recomendaciones_preventivas: {
    texto: recommendations
  },
  metadata: {
    procesado_en: new Date().toISOString(),
    version_parser: '1.7_js_fixed'
  }
};

return [{ json: output }];

// N8N Function Node - Convierte markdown de sentimientos negativos a JSON
// Obtener el texto de entrada
const inputText = $input.first().json.output || '';

// Funci√≥n para extraer mensajes
function extractMessages(text) {
  const messages = [];

  // Primero extraer solo la secci√≥n de mensajes
  const mensajesSection = extractSection(text, 'Mensajes Origen del Sentimiento Negativo');

  if (!mensajesSection) return messages;

  // Regex mejorada para capturar los mensajes correctamente
  const messageRegex = /- \*\*Pushname\*\*:\s*(.+?)\n\s*- \*\*Hora\*\*:\s*(.+?)\n\s*- \*\*Mensaje\*\*:\s*(.+?)(?=\n- \*\*Pushname\*\*|$)/gs;

  let match;
  while ((match = messageRegex.exec(mensajesSection)) !== null) {
    messages.push({
      pushname: match[1].trim(),
      hora: match[2].trim(),
      mensaje: match[3].trim()
    });
  }

  return messages;
}

// Funci√≥n para extraer secci√≥n por t√≠tulo
function extractSection(text, title) {
  const regex = new RegExp(`## ${title}\\n([\\s\\S]*?)(?=\\n## |$)`, 'i');
  const match = text.match(regex);
  return match ? match[1].trim() : '';
}

// Extraer todas las secciones
const messages = extractMessages(inputText);
const origenSentimiento = extractSection(inputText, 'Origen del Sentimiento Negativo');
const recomendacionPreventiva = extractSection(inputText, 'Recomendaci√≥n Preventiva');

// Procesar recomendaciones (convertir lista markdown a array)
function processRecommendations(text) {
  const recommendations = [];
  const lines = text.split('\n');

  for (const line of lines) {
    const trimmedLine = line.trim();
    if (trimmedLine.startsWith('- ')) {
      recommendations.push(trimmedLine.substring(2).trim());
    }
  }

  return recommendations;
}

const recomendaciones = processRecommendations(recomendacionPreventiva);

// Crear objeto de salida estructurado
const output = {
  resumen: {
    total_mensajes: messages.length,
    usuarios_involucrados: [...new Set(messages.map(m => m.pushname))],
    rango_temporal: {
      primer_mensaje: messages.length > 0 ? messages[0].hora : null,
      ultimo_mensaje: messages.length > 0 ? messages[messages.length - 1].hora : null
    }
  },
  mensajes: messages,
  analisis_sentimiento: {
    origen: origenSentimiento,
    texto_completo: origenSentimiento
  },
  recomendaciones_preventivas: {
    lista: recomendaciones,
    total: recomendaciones.length,
    texto_completo: recomendacionPreventiva
  },
  metadata: {
    procesado_en: new Date().toISOString(),
    tipo_contenido: "analisis_sentimiento_negativo",
    version_parser: "1.0"
  }
};

// Crear salidas m√∫ltiples para n8n
return [
  // Salida 1: Datos completos
  {
    json: output
  },
  // Salida 2: Solo mensajes
  {
    json: {
      mensajes: messages,
      total_mensajes: messages.length
    }
  },
  // Salida 3: Solo an√°lisis de sentimiento
  {
    json: {
      origen_sentimiento: origenSentimiento,
      usuarios_involucrados: [...new Set(messages.map(m => m.pushname))]
    }
  },
  // Salida 4: Solo recomendaciones
  {
    json: {
      recomendaciones: recomendaciones,
      total_recomendaciones: recomendaciones.length
    }
  }
];
