// Obtener los datos de entrada y parsear si es string
let mensajes = $input.first().json.mensajes_negativos || [];

// Parsear el JSON si viene como string
if (typeof mensajes === 'string') {
  try {
    mensajes = JSON.parse(mensajes);
  } catch (e) {
    console.log('Error parseando JSON:', e);
    mensajes = [];
  }
}

// Verificar que sea un array
if (!Array.isArray(mensajes)) {
  mensajes = [];
}

// Funci贸n para limpiar mensaje (remover an谩lisis y recomendaciones)
function limpiarMensaje(mensaje) {
  if (!mensaje) return '';

  // Remover todo despu茅s de "## Origen del Sentimiento Negativo"
  let mensajeLimpio = mensaje.split('## Origen del Sentimiento Negativo')[0];

  // Remover todo despu茅s de "## Recomendaci贸n Preventiva"
  mensajeLimpio = mensajeLimpio.split('## Recomendaci贸n Preventiva')[0];

  return mensajeLimpio.trim();
}

// Funci贸n para convertir markdown a HTML (simplificada para mensajes)
function convertirMarkdownAHTML(texto) {
  if (!texto) return '';

  // Limpiar el mensaje primero
  texto = limpiarMensaje(texto);

  let html = texto;

  // Convertir texto en negrita (**texto**)
  html = html.replace(/\*\*(.+?)\*\*/g, '<strong style="font-weight: bold; color: #333333;">$1</strong>');

  // Convertir texto en cursiva (*texto*)
  html = html.replace(/\*(.+?)\*/g, '<em style="font-style: italic;">$1</em>');

  // Convertir saltos de l铆nea
  html = html.replace(/\n\n/g, '</p><p style="margin: 10px 0; color: #333333; line-height: 1.5;">');
  html = html.replace(/\n/g, '<br>');

  // Envolver en p谩rrafos
  html = '<p style="margin: 10px 0; color: #333333; line-height: 1.5;">' + html + '</p>';

  return html;
}

// Funci贸n para generar el HTML de cada mensaje
function generarHTMLMensaje(chat) {
  // Convertir el mensaje de markdown a HTML
  const mensajeHTML = convertirMarkdownAHTML(chat.mensaje);

  return `<table cellpadding="0" cellspacing="0" border="0" width="100%"
 style="border: 1px solid #DDDDDD; border-radius: 8px; margin-bottom: 10px; background-color: #FAFAFA;">
 <tr>
 <td style="padding: 10px;">
 <table cellpadding="0" cellspacing="0" border="0" width="100%">
 <tr>
 <td class="message-header">
 <table cellpadding="0" cellspacing="0" border="0">
 <tr>
 <td valign="middle" style="padding-right: 5px;">
 <icon></icon>
