 </table>`;
}

// Generar HTML solo para los mensajes extra铆dos
const htmlCompleto = mensajes.map(chat => generarHTMLMensaje(chat)).join('\n');

// Retornar el resultado
return {
  json: {
    html_mensajes: htmlCompleto,
    total_mensajes: mensajes.length
  }
};

const rawText = $input.first().json.output || '';
const cleanText = rawText.replace(/\\n/g, '\n').replace(/\\t/g, '\t');

function extractMessages(text) {
  const messages = [];

  // Regex para formato - **Pushname**: valor
  const messageRegex = /- \*\*Pushname\*\*:\s*([^\n]+)\n\s*- \*\*Hora\*\*:\s*`?([^`\n]+)`?\n\s*- \*\*Mensaje\*\*:\s*([^-]*?)(?=\n- \*\*Pushname\*\*|$)/g;

  let match;
  while ((match = messageRegex.exec(text)) !== null) {
    messages.push({
      pushname: match[1].trim(),
      hora: match[2].trim(),
      mensaje: match[3].trim()
    });
  }

  return messages;
}

function extractAnalysis(text) {
  const match = text.match(/## Origen del Sentimiento Negativo\n([\s\S]*?)(?=\n### Recomendaci贸n Preventiva|\n## Recomendaci贸n Preventiva|$)/);
  let analysis = match ? match[1].trim() : '';

  // Limpiar markdown del an谩lisis
  analysis = analysis.replace(/\\n/g, '\n'); // Convertir \\n a \n
  analysis = analysis.replace(/\*\*/g, ''); // Remover **
  analysis = analysis.replace(/^#+\s*/gm, ''); // Remover # al inicio de l铆neas

  return analysis;
}

function extractRecommendations(text) {
  const match = text.match(/(?:###|##)\s*Recomendaci贸n Preventiva\n([\s\S]*?)$/);
  let recommendations = match ? match[1].trim() : '';

  // Limpiar markdown de las recomendaciones
  recommendations = recommendations.replace(/\\n/g, '\n'); // Convertir \\n a \n
  recommendations = recommendations.replace(/\*\*/g, ''); // Remover **
  recommendations = recommendations.replace(/^#+\s*/gm, ''); // Remover # al inicio de l铆neas

  return recommendations;
}

const messages = extractMessages(cleanText);
const analysis = extractAnalysis(cleanText);
const recommendations = extractRecommendations(cleanText);

// Verificar falso positivo
const isFalsePositive = (
  analysis.toLowerCase().includes('no se detecta') && 
  (analysis.toLowerCase().includes('sentimiento negativo') || analysis.toLowerCase().includes('conflicto'))
) || recommendations.toLowerCase().includes('no se requiere recomendaci贸n');

if (isFalsePositive) {
  return [{
    json: {
      resultado: 'falso_positivo',
      mensaje: 'No se detect贸 sentimiento negativo real en el contenido',
      metadata: {
        procesado_en: new Date().toISOString(),
        version_parser: '1.7_js_fixed'
      }
    }
  }];
}

const output = {
  resumen: {
    total_mensajes: messages.length,
    usuarios_involucrados: [...new Set(messages.map(m => m.pushname))],
    rango_temporal: {
      primer_mensaje: messages.length > 0 ? messages[0].hora : null,
      ultimo_mensaje: messages.length > 0 ? messages[messages.length - 1].hora : null
    }
  },
  mensajes: messages,
  analisis_sentimiento: {
    origen: analysis,
    texto_completo: analysis
  },
  recomendaciones_preventivas: {
    texto: recommendations
  },
  metadata: {
    procesado_en: new Date().toISOString(),
    version_parser: '1.7_js_fixed'
  }
};

return [{ json: output }];

// N8N Function Node - Convierte markdown de sentimientos negativos a JSON
// Obtener el texto de entrada
const inputText = $input.first().json.output || '';

// Funci贸n para extraer mensajes
function extractMessages(text) {
  const messages = [];

  // Primero extraer solo la secci贸n de mensajes
  const mensajesSection = extractSection(text, 'Mensajes Origen del Sentimiento Negativo');

  if (!mensajesSection) return messages;

  // Regex mejorada para capturar los mensajes correctamente
  const messageRegex = /- \*\*Pushname\*\*:\s*(.+?)\n\s*- \*\*Hora\*\*:\s*(.+?)\n\s*- \*\*Mensaje\*\*:\s*(.+?)(?=\n- \*\*Pushname\*\*|$)/gs;

  let match;
  while ((match = messageRegex.exec(mensajesSection)) !== null) {
    messages.push({
      pushname: match[1].trim(),
      hora: match[2].trim(),
      mensaje: match[3].trim()
    });
  }

  return messages;
}

// Funci贸n para extraer secci贸n por t铆tulo
function extractSection(text, title) {
  const regex = new RegExp(`## ${title}\\n([\\s\\S]*?)(?=\\n## |$)`, 'i');
  const match = text.match(regex);
  return match ? match[1].trim() : '';
}

// Extraer todas las secciones
const messages = extractMessages(inputText);
const origenSentimiento = extractSection(inputText, 'Origen del Sentimiento Negativo');
const recomendacionPreventiva = extractSection(inputText, 'Recomendaci贸n Preventiva');

// Procesar recomendaciones (convertir lista markdown a array)
function processRecommendations(text) {
  const recommendations = [];
  const lines = text.split('\n');

  for (const line of lines) {
    const trimmedLine = line.trim();
    if (trimmedLine.startsWith('- ')) {
      recommendations.push(trimmedLine.substring(2).trim());
    }
  }

  return recommendations;
}

const recomendaciones = processRecommendations(recomendacionPreventiva);

// Crear objeto de salida estructurado
const output = {
  resumen: {
    total_mensajes: messages.length,
    usuarios_involucrados: [...new Set(messages.map(m => m.pushname))],
    rango_temporal: {
      primer_mensaje: messages.length > 0 ? messages[0].hora : null,
      ultimo_mensaje: messages.length > 0 ? messages[messages.length - 1].hora : null
    }
  },
  mensajes: messages,
  analisis_sentimiento: {
    origen: origenSentimiento,
    texto_completo: origenSentimiento
  },
  recomendaciones_preventivas: {
    lista: recomendaciones,
    total: recomendaciones.length,
    texto_completo: recomendacionPreventiva
  },
  metadata: {
    procesado_en: new Date().toISOString(),
    tipo_contenido: "analisis_sentimiento_negativo",
    version_parser: "1.0"
  }
};

// Crear salidas m煤ltiples para n8n
return [
  // Salida 1: Datos completos
  {
    json: output
  },
  // Salida 2: Solo mensajes
  {
    json: {
      mensajes: messages,
      total_mensajes: messages.length
    }
  },
  // Salida 3: Solo an谩lisis de sentimiento
  {
    json: {
      origen_sentimiento: origenSentimiento,
      usuarios_involucrados: [...new Set(messages.map(m => m.pushname))]
    }
  },
  // Salida 4: Solo recomendaciones
  {
    json: {
      recomendaciones: recomendaciones,
      total_recomendaciones: recomendaciones.length
    }
  }
];

const response = $input.first().json;

// Mapear etiquetas complejas a categor铆as simples
const sentimentMap = {
 'negative or neutral': 'NEUTRAL',
 'positive': 'POSITIVE',
 'negative': 'NEGATIVE',
 'strongly positive': 'POSITIVE',
 'strongly negative': 'NEGATIVE'
};

// Encontrar categor铆a dominante
const highestScore = response.reduce((prev, current) => 
 current.score > prev.score ? current : prev
);

const sentiment = sentimentMap[highestScore.label];
const confidence = parseFloat(highestScore.score.toFixed(2));

// Strength = diferencia entre primero y segundo
const sortedScores = response.sort((a, b) => b.score - a.score);
const strength = parseFloat((sortedScores[0].score - sortedScores[1].score).toFixed(2));

return {
 json: {
   sentimentAnalysis: {
     category: sentiment,
     confidence: confidence,
     strength: Math.max(strength, 0.01),
     all_scores: response
   }
 }
};

const messages = $input.first().json.data;

// Solo campos relevantes
let fullText = messages
  .map(msg => `${msg.pushName}: ${msg.conversation.replace(/\\n/g, ' ').trim()}`)
  .filter(text => text.length > 5) // Filtrar mensajes vac铆os
  .join(' ');

// ltimos 512 tokens
const words = fullText.split(' ');
if (words.length > 240) {
  fullText = words.slice(-240).join(' ');
}

return {
  json: {
    text: fullText
  }
};

const messages = $input.first().json.data;

// Funci贸n para contar tokens aproximados
function countTokens(text) {
  // Tokenizer simple: espacios + puntuaci贸n
  return text.split(/[\s\.,!?;:\(\)\[\]"']+/).filter(t => t.length > 0).length;
}

let fullText = messages
  .map(msg => `${msg.pushName}: ${msg.conversation.replace(/\\n/g, ' ').trim()}`)
  .filter(text => text.length > 5)
  .join(' ');

// Truncar basado en tokens reales
let tokenCount = countTokens(fullText);
if (tokenCount > 250) {
  const words = fullText.split(' ');
  // Reducir palabra por palabra hasta estar bajo 400 tokens
  while (tokenCount > 250 && words.length > 0) {
    words.shift(); // Quitar del inicio
    fullText = words.join(' ');
    tokenCount = countTokens(fullText);
  }
}

return {
  json: {
    text: fullText,
    tokenCount: tokenCount
  }
};
