// Obtener los datos de entrada
const mensajes = $input.first().json.mensajes_negativos;

// Función para generar el HTML de cada mensaje con el globo debajo del nombre/avatar/hora, alineado a la izquierda
function generarHTMLMensaje(chat) {
  const inicial = chat.pushname ? chat.pushname.charAt(0).toUpperCase() : '?';
  return `
    <table cellpadding="0" cellspacing="0" border="0" width="100%" style="margin-bottom: 14px;">
      <tr>
        <td align="left">
          <table cellpadding="0" cellspacing="0" border="0" style="margin-bottom: 2px;">
            <tr>
              <td style="font-size:13px; color:#fff; background:#1976D2; border-radius:50%; width:28px; height:28px; text-align:center; vertical-align:middle; font-weight:bold;">${inicial}</td>
              <td style="padding-left:8px; text-align:left; vertical-align:middle;">
                <span style="font-weight:bold; color:#1976D2; font-size:13px;">${chat.pushname}</span>
                <span style="color:#888; font-size:12px; margin-left:8px;">${chat.hora}</span>
              </td>
            </tr>
          </table>
          <table cellpadding="0" cellspacing="0" border="0" width="100%">
            <tr>
              <td align="left">
                <div class="chat-bubble" style="background-color: #1976D2; color: #fff; border-radius: 18px 18px 4px 18px; padding: 12px 18px; margin-bottom: 0; max-width: 75%; font-size: 15px; line-height: 1.5; text-align: left; float: left; clear: both; box-shadow: 0 2px 8px rgba(25, 118, 210, 0.10);">
                  ${chat.mensaje}
                </div>
              </td>
            </tr>
          </table>
        </td>
        <td></td>
      </tr>
    </table>
  `;
}

// Parsear el JSON si viene como string
let chatsArray;
if (typeof mensajes === 'string') {
  chatsArray = JSON.parse(mensajes);
} else {
  chatsArray = mensajes;
}

// Generar HTML para todos los mensajes
const htmlCompleto = chatsArray.map(chat => generarHTMLMensaje(chat)).join('\n');

// Retornar el resultado
return {
  json: {
    html_mensajes: htmlCompleto
  }
};

