const resumenes = [];

for (const item of items) {
  const keys = Object.keys(item.json);

  // Ignoramos el _id y buscamos la clave que tiene el resumen real
  const resumenKey = keys.find(k => k !== '_id');

  if (resumenKey) {
    resumenes.push(resumenKey); // la clave es el resumen
  }
}

// Unimos todos los res√∫menes en un solo texto
return [
  {
    json: {
      resumenes_completos: resumenes.join("\n\n")
    }
  }
];
