// Función para procesar ambas fechas
const processDate = (input) => {
  // Procesar headers.date
  if (input.item.json.headers && input.item.json.headers.date) {
    const dateString = input.item.json.headers.date;
    const cleanedDate = dateString.substring(6, dateString.indexOf('+')).trim();
    // Convertir a formato ISO que n8n entiende mejor
    const dateObj = new Date(cleanedDate);
    input.item.json.formatted_date = dateObj.toISOString();
  }

  // Procesar hora_falla
  if (input.item.json.output && input.item.json.output.hora_falla) {
    const horaFallaStr = input.item.json.output.hora_falla;
    const timePart = horaFallaStr.substring(0, 5); // "09:55"
    const datePart = horaFallaStr.substring(horaFallaStr.indexOf("del ") + 4); // "2025-03-18"
    // Crear objeto Date y convertir a ISO
    const dateObj = new Date(`${datePart}T${timePart}:00`);
    input.item.json.formatted_hora_falla = dateObj.toISOString();
  }

  return input.item;
};

// Ejecutar función
return processDate($input);

let output = $input.item.json.output || "";
if (typeof output === 'string' && output.includes('```json')) {
  // Extrae el JSON de los delimitadores de markdown
  output = output.replace(/```json\s*|\s*```/g, '');
  // Convierte el string a objeto
  $input.item.json.output = JSON.parse(output);
}
return $input.item;
