// Función para convertir fechas españolas a timestamp PostgreSQL
const items = $input.all();

// Función para convertir fecha española a timestamp
function convertSpanishDateToTimestamp(dateStr) {
  if (!dateStr || dateStr === 'N/A') return null;

  // Mapeo de meses en español
  const monthMap = {
    'ene': 'Jan', 'feb': 'Feb', 'mar': 'Mar', 'abr': 'Apr',
    'may': 'May', 'jun': 'Jun', 'jul': 'Jul', 'ago': 'Aug',
    'sep': 'Sep', 'oct': 'Oct', 'nov': 'Nov', 'dic': 'Dec'
  };

  // Reemplazar mes español por inglés
  let englishDate = dateStr;
  for (const [spanish, english] of Object.entries(monthMap)) {
    englishDate = englishDate.replace(new RegExp(spanish, 'gi'), english);
  }

  try {
    // Convertir a formato timestamp PostgreSQL
    const date = new Date(englishDate);
    return date.toISOString().replace('T', ' ').substring(0, 19);
  } catch (error) {
    console.log(`Error converting date: ${dateStr}`, error);
    return null;
  }
}

// Procesar cada item
const processedItems = items.map(item => {
  const data = item.json;

  // Obtener datos del nodo Aggregate Information
  const aggregateData = $('Aggregate Information').first().json;

  if (aggregateData && aggregateData.data && aggregateData.data.length > 0) {
    const firstOutput = aggregateData.data[0].output;

    return {
      json: {
        ...data,
        firstOccurrenceTime: firstOutput.firstOccurrenceTime,
        lastOccurrenceTime: firstOutput.lastOccurrenceTime,
        firstOccurrenceTime_converted: convertSpanishDateToTimestamp(firstOutput.firstOccurrenceTime),
        lastOccurrenceTime_converted: convertSpanishDateToTimestamp(firstOutput.lastOccurrenceTime)
      }
    };
  }

  return item;
});

return processedItems;

const inputData = $input.all();
const outputData = [];

inputData.forEach(item => {
  let inputText = item.json.output || item.json.text || item.json.content || item.json.message || JSON.stringify(item.json);

  const firstDivIndex = inputText.indexOf('<div>');
  const lastDivIndex = inputText.lastIndexOf('</div>');

  let cleanedContent = '';

  // Verificar si hay JSON después del último </div>
  const hasJsonAfterDiv = lastDivIndex !== -1 && 
    inputText.substring(lastDivIndex + 6).trim().includes('{') && 
    inputText.substring(lastDivIndex + 6).trim().includes('}');

  if (firstDivIndex !== -1 && lastDivIndex !== -1 && firstDivIndex < lastDivIndex && hasJsonAfterDiv) {
    // Solo extraer si hay div válido Y JSON después
    cleanedContent = inputText.substring(firstDivIndex, lastDivIndex + 6);
    cleanedContent = cleanedContent.replace(/\\n/g, '').replace(/\n/g, '').replace(/\\"/g, '"').trim();
  } else {
    // Pasar el texto original si no hay nada que limpiar
    cleanedContent = inputText;
  }

  outputData.push({
    json: {
      content: cleanedContent
    }
  });
});

return outputData;

// Función para estimar tokens en N8N
function estimateTokens(content, method = 'openai') {
  if (!content || typeof content !== 'string') {
    return 0;
  }

  switch (method.toLowerCase()) {
    case 'openai':
      return estimateOpenAITokens(content);
    case 'simple':
      return estimateSimpleTokens(content);
    case 'advanced':
      return estimateAdvancedTokens(content);
    case 'claude':
      return estimateClaudeTokens(content);
    default:
      return estimateOpenAITokens(content);
  }
}

// Estimación para modelos tipo OpenAI (GPT-3.5, GPT-4)
function estimateOpenAITokens(text) {
  // Aproximación basada en estadísticas de tokenización de OpenAI
  // 1 token ≈ 4 caracteres en inglés, ~6 caracteres en español

  // Contar palabras y caracteres
  const words = text.trim().split(/\s+/).length;
  const chars = text.length;

  // Factores de ajuste
  let tokenEstimate;

  // Detectar idioma aproximado (simple heurística)
  const spanishPattern = /[ñáéíóúüç]/i;
  const isSpanish = spanishPattern.test(text);

  if (isSpanish) {
    // Para español: más caracteres por token
    tokenEstimate = Math.ceil(chars / 5.5);
  } else {
    // Para inglés y otros idiomas
    tokenEstimate = Math.ceil(chars / 4);
  }

  // Ajuste por puntuación y caracteres especiales
  const punctuation = (text.match(/[.,;:!?()[\]{}"'-]/g) || []).length;
  tokenEstimate += Math.ceil(punctuation * 0.3);

  // Ajuste por números y símbolos especiales
  const numbers = (text.match(/\d+/g) || []).length;
  tokenEstimate += Math.ceil(numbers * 0.5);

  return Math.max(1, tokenEstimate);
}

// Estimación simple basada en palabras
function estimateSimpleTokens(text) {
  const words = text.trim().split(/\s+/).length;
  // Aproximación: 1 palabra ≈ 1.3 tokens
  return Math.ceil(words * 1.3);
}

// Estimación avanzada con más factores
function estimateAdvancedTokens(text) {
  const words = text.trim().split(/\s+/);
  let tokens = 0;

  words.forEach(word => {
    // Palabras cortas (1-3 chars) = 1 token
    if (word.length <= 3) {
      tokens += 1;
    }
    // Palabras medianas (4-8 chars) = 1-2 tokens
    else if (word.length <= 8) {
      tokens += Math.ceil(word.length / 4);
    }
    // Palabras largas = más tokens
    else {
      tokens += Math.ceil(word.length / 3.5);
    }
  });

  // Ajustes adicionales
  const specialChars = (text.match(/[^\w\s]/g) || []).length;
  tokens += Math.ceil(specialChars * 0.2);

  return Math.max(1, tokens);
}

// Estimación para modelos tipo Claude
function estimateClaudeTokens(text) {
  // Claude tiende a tokenizar de manera similar a OpenAI pero con ligeras diferencias
  const baseEstimate = estimateOpenAITokens(text);
  // Claude puede ser ligeramente más eficiente en algunos casos
  return Math.ceil(baseEstimate * 0.95);
}

// Función principal para usar en N8N
function main() {
  // Obtener el contenido del input de N8N
  const inputContent = $input.first().json.body.content || '';
  const method = $input.first().json.body.method || 'openai';

  // Calcular estimación
  const estimatedTokens = estimateTokens(inputContent, method);

  // Información adicional útil
  const wordCount = inputContent.trim().split(/\s+/).length;
  const charCount = inputContent.length;

  return {
    estimated_tokens: estimatedTokens,
    word_count: wordCount,
    character_count: charCount,
    method_used: method,
    tokens_per_word: Math.round((estimatedTokens / wordCount) * 100) / 100,
    characters_per_token: Math.round((charCount / estimatedTokens) * 100) / 100
  };
}

// Para usar en N8N Code node, retorna el resultado
return main();
