const acc = $input.all();

const result = {
  data_high: [],
  data_medium: [],
  data_low: [],
  data_tbd: []
};

for (const input of acc) {
  const data = input.json;
  if (data.data_high) {
    result.data_high.push(...data.data_high);
  }
  if (data.data_medium) {
    result.data_medium.push(...data.data_medium);
  }
  if (data.data_low) {
    result.data_low.push(...data.data_low);
  }
  if (data.data_tbd) {
    result.data_tbd.push(...data.data_tbd);
  }
}

return [{ json: result }];
