// Función para n8n - Contador de Vulnerabilidades por Severidad
// Esta función analiza el JSON de entrada y cuenta vulnerabilidades por nivel

// Función principal que procesa los datos de entrada
function contarVulnerabilidades() {
    // Obtener los datos del nodo 'Clean Information'
    const inputData = $('Clean Information').first().json.output;

    // Inicializar contadores
    let contadores = {
        alta: 0,
        media: 0,
        baja: 0,
        total: 0
    };

    // Contar vulnerabilidades altas
    if (inputData.vulnerabilidades_alta && Array.isArray(inputData.vulnerabilidades_alta)) {
        contadores.alta = inputData.vulnerabilidades_alta.length;
    }

    // Contar vulnerabilidades medias
    if (inputData.vulnerabilidades_media && Array.isArray(inputData.vulnerabilidades_media)) {
        contadores.media = inputData.vulnerabilidades_media.length;
    }

    // Contar vulnerabilidades bajas
    if (inputData.vulnerabilidades_baja && Array.isArray(inputData.vulnerabilidades_baja)) {
        contadores.baja = inputData.vulnerabilidades_baja.length;
    }

    // Contar vulnerabilidades con CVSS 0 (TBD) - se consideran como medias
    let cvss_tbd = 0;
    ['vulnerabilidades_alta', 'vulnerabilidades_media', 'vulnerabilidades_baja'].forEach(nivel => {
        if (inputData[nivel] && Array.isArray(inputData[nivel])) {
            inputData[nivel].forEach(vuln => {
                if (vuln.cvss === 0) {
                    cvss_tbd++;
                }
            });
        }
    });

    // Agregar TBD al conteo de medias
    contadores.media += cvss_tbd;

    // Calcular total
    contadores.total = contadores.alta + contadores.media + contadores.baja;

    // Crear resumen con HTML divs para cada nivel
    const resumen = {
        conteo: contadores,
        html_divs: {
            alta: `<div style="font-size: 30px; font-weight: bold; color: #d9534f; margin-bottom: 5px; line-height: 1;">${contadores.alta}</div>`,
            media: `<div style="font-size: 30px; font-weight: bold; color: #f0ad4e; margin-bottom: 5px; line-height: 1;">${contadores.media}</div>`,
            baja: `<div style="font-size: 30px; font-weight: bold; color: #5cb85c; margin-bottom: 5px; line-height: 1;">${contadores.baja}</div>`,
            total: `<div style="font-size: 30px; font-weight: bold; color: #007bff; margin-bottom: 5px; line-height: 1;">${contadores.total}</div>`
        },
        resumen_textual: `Se encontraron ${contadores.total} vulnerabilidades: ${contadores.alta} altas, ${contadores.media} medias y ${contadores.baja} bajas.`,
        fecha_analisis: new Date().toISOString(),
        nivel_riesgo: determinarNivelRiesgo(contadores)
    };

    return [{ json: resumen }];
}

// Función auxiliar para determinar el nivel de riesgo general
function determinarNivelRiesgo(contadores) {
    if (contadores.alta > 0) {
        return 'CRÍTICO';
    } else if (contadores.media > 5) {
        return 'ALTO';
    } else if (contadores.media > 0) {
        return 'MEDIO';
    } else if (contadores.baja > 0) {
        return 'BAJO';
    } else {
        return 'SIN_VULNERABILIDADES';
    }
}

// Función alternativa más detallada que incluye información adicional
function contarVulnerabilidadesDetallado() {
    const inputData = $input.all();

    let contadores = {
        alta: 0,
        media: 0,
        baja: 0,
        total: 0
    };

    let detalles = {
        vulnerabilidades_por_fabricante: {},
        cves_por_nivel: {
            alta: [],
            media: [],
            baja: []
        },
        productos_afectados: new Set()
    };

    // Procesar cada item de entrada
    for (const item of inputData) {
        const data = item.json;

        // Verificar si los datos están dentro de 'output'
        const vulnerabilidadData = data.output || data;

        // Procesar vulnerabilidades altas
        if (vulnerabilidadData.vulnerabilidades_alta && Array.isArray(vulnerabilidadData.vulnerabilidades_alta)) {
            contadores.alta += vulnerabilidadData.vulnerabilidades_alta.length;
            vulnerabilidadData.vulnerabilidades_alta.forEach(vuln => {
                procesarVulnerabilidad(vuln, 'alta', detalles);
            });
        }

        // Procesar vulnerabilidades medias
        if (vulnerabilidadData.vulnerabilidades_media && Array.isArray(vulnerabilidadData.vulnerabilidades_media)) {
            contadores.media += vulnerabilidadData.vulnerabilidades_media.length;
            vulnerabilidadData.vulnerabilidades_media.forEach(vuln => {
                procesarVulnerabilidad(vuln, 'media', detalles);
            });
        }

        // Procesar vulnerabilidades bajas
        if (vulnerabilidadData.vulnerabilidades_baja && Array.isArray(vulnerabilidadData.vulnerabilidades_baja)) {
            contadores.baja += vulnerabilidadData.vulnerabilidades_baja.length;
            vulnerabilidadData.vulnerabilidades_baja.forEach(vuln => {
                procesarVulnerabilidad(vuln, 'baja', detalles);
            });
        }
    }

    contadores.total = contadores.alta + contadores.media + contadores.baja;

    // Preparar resultado final
    const resultado = {
        conteo: contadores,
        porcentajes: {
            alta: contadores.total > 0 ? Math.round((contadores.alta / contadores.total) * 100) : 0,
            media: contadores.total > 0 ? Math.round((contadores.media / contadores.total) * 100) : 0,
            baja: contadores.total > 0 ? Math.round((contadores.baja / contadores.total) * 100) : 0
        },
        detalles: {
            ...detalles,
            productos_afectados: Array.from(detalles.productos_afectados),
            fabricantes_con_mas_vulnerabilidades: obtenerTopFabricantes(detalles.vulnerabilidades_por_fabricante)
        },
        nivel_riesgo: determinarNivelRiesgo(contadores),
        fecha_analisis: new Date().toISOString()
    };

    return [{ json: resultado }];
}

// Función auxiliar para procesar cada vulnerabilidad individualmente
function procesarVulnerabilidad(vuln, nivel, detalles) {
    if (vuln.fabricante) {
        if (!detalles.vulnerabilidades_por_fabricante[vuln.fabricante]) {
            detalles.vulnerabilidades_por_fabricante[vuln.fabricante] = { alta: 0, media: 0, baja: 0 };
        }
        detalles.vulnerabilidades_por_fabricante[vuln.fabricante][nivel]++;
    }

    if (vuln.cve) {
        detalles.cves_por_nivel[nivel].push(vuln.cve);
    }

    if (vuln.producto) {
        detalles.productos_afectados.add(vuln.producto);
    }
}

// Función auxiliar para obtener los fabricantes con más vulnerabilidades
function obtenerTopFabricantes(fabricantes) {
    return Object.entries(fabricantes)
        .map(([fabricante, conteos]) => ({
            fabricante,
            total: conteos.alta + conteos.media + conteos.baja,
            desglose: conteos
        }))
        .sort((a, b) => b.total - a.total)
        .slice(0, 5);
}

// Exportar la función principal (usar una de las dos funciones según necesidad)
return contarVulnerabilidades(); // Para análisis básico
