// C√≥digo para n8n que convierte markdown a HTML formateado para email
// La funci√≥n recibe el contenido en $input.first().json.text

// Funci√≥n principal para convertir markdown a HTML
function formatMarkdownToHtml(markdown) {
  if (!markdown) return '';

  // Obtener el nombre del cliente del campo espec√≠fico de n8n
  // Si no est√° disponible, intentar extraerlo del texto markdown
  let clientName = $('On form submission').first().json['Nombre del Cliente'] || "Cliente";

  // Si el campo de formulario no existe o est√° vac√≠o, intentar extraerlo del markdown
  if (!clientName || clientName === "Cliente") {
    const clientMatch = markdown.match(/\*\*\s*Solicitante:\s*(.*?)\s*\*\*/);
    if (clientMatch && clientMatch[1]) {
      // Eliminar texto adicional despu√©s del nombre del cliente si existe
      // Por ejemplo "Walmart Centroam√©rica Este nombre se utiliza..."
      let extractedName = clientMatch[1].split(/\s+Este nombre/)[0];
      extractedName = extractedName.split(/\s+este nombre/)[0];
      clientName = extractedName.trim();
    }
  }

  // Formatear t√≠tulos principales
  let html = markdown.replace(/^# (.*?)$/gm, '<h1 style="font-size: 24px; color: #0071ce; border-bottom: 2px solid #0071ce; padding-bottom: 10px; margin-top: 25px; margin-bottom: 15px;">$1</h1>');

  // Formatear subt√≠tulos
  html = html.replace(/^## (.*?)$/gm, '<h2 style="font-size: 18px; color: #041e42; margin-top: 20px; margin-bottom: 10px; border-left: 4px solid #0071ce; padding-left: 10px;">$1</h2>');

  // Formatear negritas
  html = html.replace(/\*\*(.*?)\*\*/g, '<strong style="font-weight: bold; color: #041e42;">$1</strong>');

  // Formatear listas con vi√±etas
  html = html.replace(/^- (.*?)$/gm, '<li style="margin-bottom: 5px; font-size: 16px;">$1</li>');

  // Detectar y agrupar elementos de lista en forma segura
  const lines = html.split('\n');
  let result = [];
  let currentList = [];

  for (let i = 0; i < lines.length; i++) {
    const line = lines[i];
    if (line.startsWith('<li')) {
      currentList.push(line);
    } else {
      if (currentList.length > 0) {
        result.push('<ul style="margin-left: 10px; padding-left: 20px; margin-bottom: 10px;">');
        result = result.concat(currentList);
        result.push('</ul>');
        currentList = [];
      }
      result.push(line);
    }
  }

  // Si qued√≥ alguna lista sin cerrar
  if (currentList.length > 0) {
    result.push('<ul style="margin-left: 10px; padding-left: 20px; margin-bottom: 10px;">');
    result = result.concat(currentList);
    result.push('</ul>');
  }

  html = result.join('\n');

  // Reemplazar saltos de l√≠nea con etiquetas <br/>
  html = html.replace(/\n/g, '<br/>');

  // Limpiar <br/> dobles despu√©s de etiquetas de cierre
  html = html.replace(/<\/h1><br\/>/g, '</h1>');
  html = html.replace(/<\/h2><br\/>/g, '</h2>');
  html = html.replace(/<\/ul><br\/>/g, '</ul>');

  // Agregar disclaimer al final
  const disclaimer = `
  <div style="margin-top: 40px; padding: 15px; background-color: #f5f5f5; border-left: 4px solid #0071ce; font-size: 24px;">
    <p><strong>Aviso Importante:</strong> Este es un resumen general destinado a proporcionar un contexto inicial sobre el RFP. Para una comprensi√≥n completa y detallada, se recomienda revisar el documento RFP original en su totalidad. Este an√°lisis no sustituye la revisi√≥n t√©cnica y legal exhaustiva del documento completo.</p>
  </div>
  `;

  // Crear contenedor principal con encabezado de IFX NETWORKS
  const formattedHTML = `
  <!DOCTYPE html>
  <html>
  <head>
    <meta charset="UTF-8">
    <title>An√°lisis RFP</title>
    <style>
      body {
        font-family: Arial, sans-serif;
        line-height: 1.6;
        color: #333;
        max-width: 800px;
        margin: 0 auto;
        padding: 20px;
      }
      .header {
        background-color: #041e42;
        color: white;
        padding: 30px;
        text-align: center;
        margin-bottom: 5px;
        border-radius: 8px;
        box-shadow: 0 4px 8px rgba(0,0,0,0.2);
      }
      .header h1 {
        font-size: 32px !important;
        margin: 0 !important;
        padding: 0 !important;
        color: white !important;
        border: none !important;
      }
      .header p {
        font-size: 22px;
        margin-top: 10px;
        margin-bottom: 0;
        color: #78d64b;
      }
      .powered-by {
        text-align: center;
        margin: 5px 0 15px 0;
        padding: 8px;
        font-size: 14px;
      }
      .powered-by strong em {
        font-weight: bold;
        font-style: italic;
        font-family: Arial, Helvetica, sans-serif;
        color: #cc00cc;
      }
      h1 {
        font-size: 24px;
        color: #0071ce;
        border-bottom: 2px solid #0071ce;
        padding-bottom: 10px;
        margin-top: 25px;
        margin-bottom: 15px;
      }
      h2 {
        font-size: 18px;
        color: #041e42;
        margin-top: 20px;
        margin-bottom: 10px;
        border-left: 4px solid #0071ce;
        padding-left: 10px;
      }
      ul {
        margin-left: 10px;
        padding-left: 20px;
        margin-bottom: 8px;
      }
      li {
        margin-bottom: 5px;
        font-size: 16px;
      }
      /* Para listas anidadas */
      li li {
        font-size: 15px;
        margin-bottom: 3px;
      }
      strong {
        font-weight: bold;
        color: #041e42;
      }
      .section {
        background-color: #f9f9f9;
        padding: 15px;
        border-radius: 5px;
        margin-bottom: 20px;
      }
      .recommendations {
        background-color: #f0fff0;
      }
      .risks {
        background-color: #fff0f0;
      }
    </style>
  </head>
  <body>
    <div class="header">
      <h1>IFX NETWORKS</h1>
      <p>An√°lisis de RFP para ${clientName}</p>
    </div>

    <div class="powered-by">
      Generated by <strong><em>aifx</em></strong>
    </div>

    ${html}

    ${disclaimer}
  </body>
  </html>`;

  return formattedHTML;
}

// Este es el formato correcto para n8n
return [
  {
    json: {
      markdownText: $input.first().json.text,
      formattedHtml: formatMarkdownToHtml($input.first().json.text),
      plainText: "Este correo contiene HTML formateado. Si no puede verlo correctamente, utilice un cliente de correo que soporte HTML."
    }
  }
];
