const rawData =$json.output ;

// El formato del input parece ser un string JSON con comillas al inicio y final
// Primero limpiamos posibles comillas extras al inicio y final
let cleanData = rawData;
if (typeof rawData === 'string') {
  // Eliminar comillas dobles al inicio y final si existen
  cleanData = rawData.trim();
  if (cleanData.startsWith('"') && cleanData.endsWith('"')) {
    cleanData = cleanData.substring(1, cleanData.length - 1);
  }

  // Reemplazar posibles escapes de comillas dobles
  cleanData = cleanData.replace(/\\"/g, '"');
}

// Intentamos parsear el JSON
let parsedData;
try {
  parsedData = JSON.parse(cleanData);
} catch (error) {
  console.error('Error al parsear JSON:', error);
  // Si falla, devolvemos un array vacío o manejamos el error según necesidad
  parsedData = [];
}

// Aseguramos que tenemos un array para mapear
const itemsArray = Array.isArray(parsedData) ? parsedData : [parsedData];

return itemsArray.map(item => {
  // Objeto de resultado con las propiedades requeridas
  const result = {
    json: {
      nombreChat: item.nombrechat || '',
      sentimientoNegativo: item.sentimiento_negativo_detectado || false,
      puntuacionNegatividad: item.negativity_score || 0,
      nivelGravedad: item.nivel_gravedad || '',
      tipoProblema: item.tipo_de_problema || '',
      mensajesProblema: item.mensajes_problema || [],
      comentarioInteraccion: item.comentario_interaccion || '',
      executionid:$execution.id,

      // Extraemos los detalles de todos los mensajes
      mensajesDetalle: (item.mensajes_problema || []).map(mensaje => ({
        id: mensaje.id || '',
        fecha: mensaje.dateTime || '', // Notamos que ahora usa 'dateTime' en lugar de 'fecha'
        texto: mensaje.texto || '',
        autor: mensaje.autor || '',
        numero: mensaje.numero || ''
      }))
    }
  };

  return result;
});

