const markdownText = $input.first().json.markdown || $input.first().json.output || '';
const formattedHtml = formatMarkdownToHtml(markdownText);

// DEVOLVER RESULTADO PARA LOS SIGUIENTES NODOS (guardar / enviar)
return [
	{
		json: {
			markdownText,
			formattedHtml,
		},
	},
];

// Genera un session_id tipo: "20240519-168957-a8f23"
const timestamp = new Date().toISOString().replace(/[-:.TZ]/g, '').slice(0, 14);
const random = Math.floor(Math.random() * 1000000).toString().padStart(6, '0');
const sessionId = `session-${timestamp}-${random}`;
const numberOfFiles = items.length;

return items.map(item => ({
  json: {
    ...item.json,
    session_id: sessionId,
    number_of_files_received: numberOfFiles
  },
  binary: item.binary
}));

// Iteramos sobre los archivos recibidos
const files = $input.all();

const maxFileSize = 20 * 1024 * 1024; // 20MB en bytes
const maxFiles = 6;  // N√∫mero m√°ximo de archivos por sesi√≥n

// Verificamos el n√∫mero de archivos
if (files.length > maxFiles) {
  throw new Error(`Se permiten un m√°ximo de ${maxFiles} archivos por sesi√≥n.`);
}

// Verificamos el tama√±o de cada archivo
for (let i = 0; i < files.length; i++) {
  const file = files[i];

  if (file.binary && file.binary.data) {
    const fileSize = Buffer.byteLength(file.binary.data);

    if (fileSize > maxFileSize) {
      throw new Error(`El archivo ${file.json.fileName} excede el tama√±o m√°ximo de 20MB.`);
    }
  }
}

return items; // Continuamos con el flujo si todo est√° bien

/**
 * Este nodo debe ejecutarse en modo ‚ÄúRun Once for All Items‚Äù
 * (Output: Many ‚Üí 1-n).
 */

/* ------------------------------------------------------------------
   1. Tomamos el primer item de la entrada (contiene todos los binarios)
------------------------------------------------------------------- */
const inputItem   = $input.first();
const binaryDocs  = inputItem.binary;          // { Document0, Document1, ‚Ä¶ }
const baseJson    = inputItem.json;            // metadatos que ya tra√≠a
const sessionId   = baseJson.session_id        // ya existe‚Ä¶
                  || `session-${Date.now()}`;  // ‚Ä¶o generamos uno

/* ------------------------------------------------------------------
   2. Creamos un item por cada archivo binario
------------------------------------------------------------------- */
const newItems = Object.entries(binaryDocs).map(([fieldName, bin]) => {

	// clon profundo para NO perder la propiedad data
	const clonedBinary = JSON.parse(JSON.stringify(bin));
    const numberOfBinaryElements = Object.keys(binaryDocs).length;

	return {
		json: {
			...baseJson,
			session_id   : sessionId,
			document_name: clonedBinary.fileName,
			mimeType     : clonedBinary.mimeType,
			binaryField  : fieldName,          // ¬´Document0¬ª, ¬´Document1¬ª, etc.
            numberOfFiles : numberOfBinaryElements,
		},
		binary: {
			[fieldName]: clonedBinary         // el binario completo
		}
	};
});

/* ------------------------------------------------------------------
   3. Devolvemos los nuevos items; cada uno mantiene su propio binario
------------------------------------------------------------------- */
return newItems;

/********************************************************************
 * Verifica si ya existe la colecci√≥n Qdrant para el cliente.
 * ‚Äî `$input.first().json.result.collections`  ‚áí lista devuelta por Qdrant
 * ‚Äî `$('Preparar Creaci√≥n').first().json.collectionName` ‚áí nombre deseado
 * Devuelve un √∫nico item con { collectionExists: true | false }
 ********************************************************************/

// 1. Obtener la lista de colecciones devuelta por el nodo Qdrant
const collectionsData = $input.first().json.result.collections;

// 2. Obtener el nombre de la colecci√≥n que queremos crear / usar
const clientCollectionName = $('Webhook').first().json.body.empresa;

// 3. Validar formato y buscar coincidencia
const collectionExists = Array.isArray(collectionsData)
  ? collectionsData.some(col => col?.name === clientCollectionName)
  : false;

// 4. Devolver resultado en un solo item
return [
  {
    json: { collectionExists,
           clientCollectionName
          }
  }
];
