function formatMarkdownToHtml(markdown) {
  if (!markdown) return '';

  // Obtener el nombre del cliente del campo espec√≠fico de n8n
  let clientName = $input.first().json['Nombre del Cliente'] || "Cliente";

  // Si el campo de formulario no existe o est√° vac√≠o, intentar extraerlo del markdown
  if (!clientName || clientName === "Cliente") {
    const clientMatch = markdown.match(/\*\*\s*Solicitante:\s*(.*?)\s*\*\*/);
    if (clientMatch && clientMatch[1]) {
      let extractedName = clientMatch[1].split(/\s+Este nombre/)[0];
      extractedName = extractedName.split(/\s+este nombre/)[0];
      clientName = extractedName.trim();
    }
  }

  // Formatear t√≠tulos principales
  let html = markdown.replace(/^# (.*?)$/gm, (_, titleText, offset, fullText) => {
    const index = fullText.slice(0, offset).match(/^# /gm)?.length || 0;

    const styles = [
      'font-size: 32px; color: #0a1f44; font-weight: 800; margin: 30px 0 12px; padding-bottom: 6px; border-bottom: 3px solid #0071ce; line-height: 1.2; font-family: Segoe UI, Roboto, sans-serif;',
      'font-size: 28px; color: #123c69; font-weight: 700; margin: 26px 0 10px; padding-bottom: 4px; border-bottom: 2px solid #0071ce; line-height: 1.2;',
      'font-size: 24px; color: #1a1a1a; font-weight: 600; margin: 22px 0 8px; padding-bottom: 2px; border-bottom: 1px solid #ccc; line-height: 1.2;',
      'font-size: 22px; color: #333; font-weight: 600; margin: 18px 0 6px; line-height: 1.2;',
    ];

    const style = styles[Math.min(index, styles.length - 1)];
    return `<h1 style="${style}">${titleText}</h1>`;
  });

  // Formatear subt√≠tulos
  html = html.replace(/^## (.*?)$/gm, '<h2 style="font-size: 18px; color: #041e42; margin-top: 20px; margin-bottom: 10px; border-left: 4px solid #0071ce; padding-left: 10px;">$1</h2>');

  // Formatear negritas
  html = html.replace(/\*\*(.*?)\*\*/g, '<strong style="font-weight: bold; color: #041e42;">$1</strong>');

  // Formatear listas con vi√±etas
  html = html.replace(/^- (.*?)$/gm, '<li style="margin-bottom: 5px; font-size: 16px;">$1</li>');

  // Detectar y agrupar elementos de lista en forma segura
  const lines = html.split('\n');
  let result = [];
  let currentList = [];

  for (let i = 0; i < lines.length; i++) {
    const line = lines[i];
    if (line.startsWith('<li')) {
      currentList.push(line);
    } else {
      if (currentList.length > 0) {
        result.push('<ul style="margin-left: 10px; padding-left: 20px; margin-bottom: 10px;">');
        result = result.concat(currentList);
        result.push('</ul>');
        currentList = [];
      }
      result.push(line);
    }
  }

  if (currentList.length > 0) {
    result.push('<ul style="margin-left: 10px; padding-left: 20px; margin-bottom: 10px;">');
    result = result.concat(currentList);
    result.push('</ul>');
  }

  html = result.join('\n');

  // Convertir tablas Markdown a HTML
  html = html.replace(/((?:^\|.*\|\s*\n?)+)/gm, match => {
    const rows = match.trim().split('\n');

    // Separar filas en celdas y limpiar espacios
    const parsedRows = rows.map(row => row.trim().split('|').filter(cell => cell.trim() !== ''));

    const headers = parsedRows[0];
    const isDividerRow = row => row.every(cell => /^-+$/.test(cell));
    const bodyRows = parsedRows.slice(1).filter(row => !isDividerRow(row));

    // Si la √∫ltima cabecera est√° vac√≠a, eliminarla
    const cleanHeaders = headers.filter(h => h.trim() !== '');

    const thead = `<thead><tr>${cleanHeaders.map(h => `<th>${h.trim()}</th>`).join('')}</tr></thead>`;
    const tbody = `<tbody>${bodyRows.map(row => {
      return `<tr>${row.slice(0, cleanHeaders.length).map(c => `<td>${c.trim()}</td>`).join('')}</tr>`;
    }).join('')}</tbody>`;

    return `<table class="markdown-table">${thead}${tbody}</table>`;
  });

  // Reemplazar saltos de l√≠nea con <br/>
  html = html.replace(/\n/g, '<br/>');
  html = html.replace(/<\/h1><br\/>/g, '</h1>');
  html = html.replace(/<\/h2><br\/>/g, '</h2>');
  html = html.replace(/<\/ul><br\/>/g, '</ul>');

  // HTML Final con estilos IFX
  const formattedHTML = `
  <!DOCTYPE html>
  <html lang="es">
  <head>
    <meta charset="UTF-8">
    <title>Ejemplo 2</title>
    <style>
      body {
        font-family: 'Arial', sans-serif;
        background-color: #f4f6f8;
        margin: 0;
        color: #2c3e50;
      }
      .container {
        max-width: 700px;
        margin: 40px auto;
        background: #fff;
        border-radius: 8px;
        box-shadow: 0 2px 8px rgba(44,62,80,0.07);
        padding: 0 0 30px 0;
      }
      .header {
        padding: 40px 40px 20px 40px;
        border-bottom: 1px solid #e9ecef;
        text-align: left;
        background: #232a34;
        border-radius: 8px 8px 0 0;
      }
      .header-row {
        display: flex;
        align-items: center;
        gap: 28px;
      }
      .header-titles {
        display: flex;
        flex-direction: column;
        gap: 2px;
      }
      .header-titles .title,
      .header-title-main {
        color: #fff;
      }
      .header-titles .title {
        font-size: 24px;
        font-weight: 600;
        margin-bottom: 2px;
      }
      .header-title-main {
        font-size: 28px;
        font-weight: 700;
        margin-bottom: 0;
      }
      .logo {
        max-width: 180px;
        height: auto;
        margin-bottom: 20px;
      }
      .markdown-table {
        border-collapse: collapse;
        width: 100%;
        margin: 20px 0;
        font-size: 14px;
      }
      .markdown-table th,
      .markdown-table td {
        border: 1px solid #ccc;
        padding: 8px 12px;
        text-align: left;
      }
      .markdown-table th {
        background-color: #0071ce;
        color: #fff;
      }
      .markdown-table tr:nth-child(even) td {
        background-color: #f9f9f9;
      }
      .footer {
        background: #232a34;
        color: #e0e0e0;
        padding: 32px 40px 24px 40px;
        border-radius: 0 0 8px 8px;
        font-size: 13px;
        text-align: left;
        margin-top: 32px;
      }
      .footer-logo {
        max-width: 110px;
        height: auto;
        margin-bottom: 10px;
      }
      .footer-title {
        font-weight: 700;
        color: #fff;
        margin-bottom: 4px;
        font-size: 15px;
      }
      .footer-row {
        display: flex;
        gap: 32px;
        margin-top: 12px;
      }
      .footer-info {
        flex: 1;
        min-width: 0;
        line-height: 1.7;
        margin-top: 0;
        font-size: 13px;
        color: #e0e0e0;
      }
    </style>
  </head>
  <body>
    <div class="container">
      <div class="header">
        <div class="header-row">
          <img src="https://ifxnetworks.com/hubfs/IFX/logo-ifx--primary.svg" alt="IFX Networks" class="logo">
          <div class="header-titles">
            <div class="title">Ejemplo 2</div>
            <div class="header-title-main">Subt√≠tulo o nombre de proyecto</div>
          </div>
        </div>
        <div style="font-size:15px; color:#d2d2d2; margin-bottom:10px;">
          Breve descripci√≥n del prop√≥sito de este documento.
        </div>
      </div>
      <div class="markdown-content" style="padding: 30px 40px;">
        ${html}
      </div>
      <div class="footer">
        <img src="https://ifxnetworks.com/hubfs/IFX/logo-ifx--primary.svg" alt="IFX Networks" class="footer-logo">
        <div class="footer-title">IFX Networks</div>
        <div class="footer-row">
          <div class="footer-info">
            25 a√±os de experiencia en el mercado de las Telecomunicaciones.
          </div>
          <div class="footer-info">
            Diagonal 97 No. 17 ‚Äì 60 piso 4<br>
            Edificio Centro Empresarial Corporativo<br>
            Bogot√°, Colombia
          </div>
          <div class="footer-info">
            +57 (601) 369 3000<br>
            CARE: +57 (601) 369 3024
          </div>
        </div>
      </div>
