const intentosMaximos = 10;
const intentoActual = $json.intento || 1;

if (intentoActual > intentosMaximos) {
  throw new Error('Se alcanz√≥ el l√≠mite de reintentos');
}

return items.map(item => ({
  json: {
    ...item.json,
    intento: intentoActual + 1
  }
}));

// Genera un session_id tipo: "20240519-168957-a8f23"
const timestamp = new Date().toISOString().replace(/[-:.TZ]/g, '').slice(0, 14);
const random = Math.floor(Math.random() * 1000000).toString().padStart(6, '0');
const sessionId = `session-${timestamp}-${random}`;

return items.map(item => ({
  json: {
    ...item.json,
    session_id: sessionId
  },
  binary: item.binary
}));

const resumen = $json.resumen || '';
const escapeHtml = (unsafe) =>
  unsafe
    .replace(/&/g, '&amp;')
    .replace(/</g, '&lt;')
    .replace(/>/g, '&gt;')
    .replace(/"/g, '&quot;')
    .replace(/'/g, '&#039;');

return [
  {
    json: {
      ...$json,
      resumen_html: `<div>${escapeHtml(resumen)}</div>`
    }
  }
];

// Iteramos sobre los archivos recibidos
const files = $input.all();

const maxFileSize = 20 * 1024 * 1024; // 20MB en bytes
const maxFiles = 6;  // N√∫mero m√°ximo de archivos por sesi√≥n

// Verificamos el n√∫mero de archivos
if (files.length > maxFiles) {
  throw new Error(`Se permiten un m√°ximo de ${maxFiles} archivos por sesi√≥n.`);
}

// Verificamos el tama√±o de cada archivo
for (let i = 0; i < files.length; i++) {
  const file = files[i];

  if (file.binary && file.binary.data) {
    const fileSize = Buffer.byteLength(file.binary.data);

    if (fileSize > maxFileSize) {
      throw new Error(`El archivo ${file.json.fileName} excede el tama√±o m√°ximo de 20MB.`);
    }
  }
}

return items; // Continuamos con el flujo si todo est√° bien

// Obtener los documentos binarios del input
const binaryDocs = $binary;

// Separar los documentos binarios en items individuales
const items = [];
Object.keys(binaryDocs).forEach((key, index) => {
    const document = binaryDocs[key];  // Obtener el archivo binario

    // Crear un nuevo item para cada documento
    items.push({
        json: {
            session_id: $input.first().json.session_id, // Asignar el mismo session_id
            document_name: document.fileName, // Agregar el nombre del archivo
            mimeType: document.mimeType, // Tipo MIME del archivo
        },
        binary: {
            // Mantener la referencia al archivo binario, sin perder el "Document0" o "Document1"
            [key]: document // Esta es la clave binaria que se mantiene (Document0, Document1, etc.)
        }
    });
});

// Devolver los nuevos items para ser procesados en el Loop over Items
return items;
